import { Component, h, Prop } from '@stencil/core';
import { abbreviate } from './format';

/**
 * @exampleComponent limel-example-badge
 */
@Component({
    tag: 'limel-badge',
    styleUrl: 'badge.scss',
    shadow: true,
})
export class Badge {
    /**
     * Label to display in the badge
     */
    @Prop({ reflect: true })
    public label: number;

    render() {
        return <div class="badge-container">{abbreviate(this.label)}</div>;
    }
}

@import '../../style/internal/mdc-variables';

/**
 * @prop --badge-background-color: badge background color
 * @prop --badge-text-color: badge text color
 */

:host {
    display: block;
}

:host([hidden]) {
    display: none;
}

* {
    box-sizing: border-box;
}

.badge-container {
    display: inline-block;
    font-size: pxToRem(11);
    min-width: pxToRem(20);
    height: pxToRem(20);
    border-radius: pxToRem(20);
    line-height: pxToRem(20);
    text-align: center;
    background-color: var(--badge-background-color, #eef1f5);
    color: var(--badge-text-color, #7d899c);
    padding: 0 pxToRem(5);
    margin-left: pxToRem(4);
}

import { Component, h, Method, Prop, State } from '@stencil/core';

/**
 * @exampleComponent limel-example-banner
 * @slot buttons - Buttons to show in the banner
 */
@Component({
    tag: 'limel-banner',
    shadow: true,
    styleUrl: 'banner.scss',
})
export class Banner {
    /**
     * The text to show on the banner.
     */
    @Prop({ reflect: true })
    public message: string;

    /**
     * Set icon for the banner
     */
    @Prop({ reflect: true })
    public icon: string;

    @State()
    private isOpen = false;

    /**
     * Open the banner
     */
    @Method()
    public async open() {
        this.isOpen = true;
    }

    /**
     * Close the banner
     */
    @Method()
    public async close() {
        this.isOpen = false;
    }

    public render() {
        return (
            <div
                class={`lime-banner ${this.isOpen ? 'lime-banner--open' : ''}`}
            >
                <div class="lime-banner__surface">
                    <div class="lime-banner__content">
                        {this.renderIcon()}
                        <div class="mdc-typography--body2">{this.message}</div>
                        <div class="lime-banner__actions">
                            <slot name="buttons" />
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    private renderIcon(): HTMLElement {
        if (!this.icon) {
            return;
        }

        return (
            <div class="lime-banner__icon">
                <limel-icon name={this.icon} badge={true} size="large" />
            </div>
        );
    }
}

@import '@limetech/mdc-elevation/mixins';
@import '@limetech/mdc-theme/mixins';
@import '../../style/internal/mdc-variables';

.lime-banner {
    min-height: $lime-banner-height;
    @include mdc-theme-prop(background-color, surface);
    @include mdc-elevation(1);
    display: none;
    &.lime-banner--open {
        display: block;
        .lime-banner__surface {
            opacity: 1;
        }
    }
    .lime-banner__surface {
        display: flex;
        justify-content: center;
        box-sizing: border-box;
        opacity: 0;
    }

    .lime-banner__content {
        display: flex;
        align-items: center;
        align-content: stretch;
        padding: pxToRem(16);
        flex-wrap: wrap;
    }

    .lime-banner__icon {
        margin: 0 pxToRem(10);
        align-self: flex-start;
    }

    .lime-banner__actions {
        margin-left: pxToRem(50);
        display: flex;
        justify-content: flex-end;
    }

    limel-icon {
        color: white;
        background-color: var(--icon-background-color, var(--lime-dark-grey));
    }
}

import { Component, Element, h, Prop, Watch } from '@stencil/core';

/**
 * @exampleComponent limel-example-button
 * @exampleComponent limel-example-button-click
 * @exampleComponent limel-example-button-reduce-presence
 * @exampleComponent limel-example-button-colors
 */
@Component({
    tag: 'limel-button',
    shadow: true,
    styleUrl: 'button.scss',
})
export class Button {
    /**
     * The text to show on the button.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Set to `true` to make the button primary.
     */
    @Prop({ reflect: true })
    public primary = false;

    /**
     * Set to `true` to make the button outlined.
     */
    @Prop({ reflect: true })
    public outlined = false;

    /**
     * Set icon for the button
     */
    @Prop({ reflect: true })
    public icon: string;

    /**
     * Set to `true` to disable the button.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Set to `true` to put the button in the `loading` state.
     * Please note that this does _not_ disable the button.
     * If the button should be disabled while loading, the
     * `disabled` property should be set to `true` as well.
     */
    @Prop({ reflect: true })
    public loading = false;

    @Element()
    private limelButton: HTMLLimelButtonElement;

    private justLoadedTimeout: NodeJS.Timeout;

    public render() {
        return (
            <button
                class={`
                    mdc-button
                    ${this.primary ? 'mdc-button--unelevated' : ''}
                    ${this.loading ? 'loading' : ''}
                    ${this.outlined ? 'mdc-button--outlined' : ''}
                `}
                disabled={this.disabled}
            >
                <div class="mdc-button__ripple" />
                {this.renderIcon()}
                <span class="label mdc-button__label">{this.label}</span>
                <limel-spinner />
                <svg viewBox="0 0 30 30">
                    <path d="M20.659 10l-6.885 6.884-3.89-3.89-1.342 1.341 5.053 5.052.182.176L22 11.341z" />
                </svg>
            </button>
        );
    }

    @Watch('loading')
    protected loadingWatcher(newValue: boolean, oldValue: boolean) {
        const JUST_LOADED = 'just-loaded';
        const button = this.limelButton.shadowRoot.querySelector('button');
        if (oldValue && !newValue) {
            button.classList.remove('loading');
            button.classList.add(JUST_LOADED);
            const TIMEOUT = 2000;
            this.justLoadedTimeout = setTimeout(() => {
                button.classList.remove(JUST_LOADED);
            }, TIMEOUT);
        } else if (newValue) {
            button.classList.remove(JUST_LOADED);
            clearTimeout(this.justLoadedTimeout);
        }
    }

    private renderIcon(): HTMLElement {
        if (!this.icon) {
            return;
        }

        let withoutLabelClass = '';
        if (!this.label) {
            withoutLabelClass = 'no-label';
        }

        return (
            <i class={`mdc-button__icon ${withoutLabelClass}`}>
                <limel-icon name={this.icon} />
            </i>
        );
    }
}

@import '../../style/internal/mdc-variables';
@import '@limetech/mdc-button/mdc-button';
@import '../../style/mixins';
@import '../../style/internal/variables';

:host {
    display: inline-block;
}

:host([hidden]) {
    display: none;
}

:host([disabled]) {
    pointer-events: none;
}

button {
    @include is-elevated-clickable();

    &.mdc-button {
        min-width: pxToRem(36);
        padding: 0 pxToRem(12);
        height: 100%;
        min-height: pxToRem(36);
        width: 100%;
    }

    .label {
        opacity: 1;
    }

    limel-spinner {
        opacity: 0;
        display: none;
        position: absolute;
    }

    .mdc-button__icon.no-label {
        margin: 0;
    }

    limel-icon {
        vertical-align: top;
    }

    svg {
        fill: currentColor;
        height: pxToRem(30);
        opacity: 0;
        pointer-events: none;
        position: absolute;
        width: pxToRem(30);
    }

    limel-icon,
    .label,
    limel-spinner,
    svg {
        transition: opacity 300ms ease-in-out;
    }

    &.loading {
        limel-icon,
        .label {
            opacity: 0;
        }
        limel-spinner {
            opacity: 1;
            display: block;
        }
    }

    &.just-loaded {
        limel-icon,
        .label {
            opacity: 0;
        }
        svg {
            opacity: 1;
        }
        limel-spinner {
            display: block;
        }
    }
}

$speed-of-reducing-presence: 0.3s;

@keyframes reduced-presence {
    0% {
        opacity: 1;
        transform: scale(1);
    }
    100% {
        opacity: 0;
        transform: scale(0.7);
    }
}

:host(.has-reduced-presence) {
    button {
        &[disabled]:not(.loading):not(.just-loaded) {
            animation: reduced-presence $speed-of-reducing-presence ease
                forwards;
            transition: padding $speed-of-reducing-presence ease,
                min-width $speed-of-reducing-presence ease;
            transition-delay: $speed-of-reducing-presence;

            padding: 0;
            min-width: 0;
            // we don't animate `min-height` and reset it to `0`, otherwise the
            // button completely dissapear physically and can sometimes layout-shifts
            // in the UI

            .label {
                transition: font-size 0.8s ease;
                transition-delay: $speed-of-reducing-presence;
                font-size: 0;
                opacity: 0;
            }
            limel-icon,
            limel-spinner,
            svg {
                transition: all $speed-of-reducing-presence ease;
                transition-delay: $speed-of-reducing-presence;

                opacity: 0;
                width: 0;
                height: 0;
            }
        }
    }
}

import { Component, Event, EventEmitter, h, Prop, State } from '@stencil/core';
import { Button } from '../button/button.types';
import { createRandomString } from '../../util/random-string';

/**
 * A button group control is a linear set of two or more buttons.
 *
 * ## Usage
 *
 * Button groups are often used to display different views of the same thing. A
 * common example of this component is when you switch between [ Map | Transit
 * | Satellite ] views to look at an area on the map.
 *
 * In some cases, button groups may serve as quick filters as well. For example
 * a list of contacts, in which the user can switch to [ All | Favorites
 * | Frequently contacted ] can incorporate a button group to quickly filter out
 * items and display subsets of them.
 *
 * ## Layout
 *
 * The button groups are usually placed in top headers and action bars,
 * sometimes with other elements. Since the group items will always be rendered
 * in a row, you must make sure not to have too many buttons in the group.
 * Because if the container of your button group does not get enough space to
 * fit in all its buttons, they will have to truncate their text and may appear
 * very cramped together. Always think about how your button group will appear
 * on a small screen such as phones.
 * :::note
 * Button can contain text or icons, but not both simultaneously!
 * :::
 *
 * Within the group, icon buttons will all have the same width, while each text button
 * inherits its width from its content.
 *
 * @exampleComponent limel-example-button-group-icons
 * @exampleComponent limel-example-button-group
 * @exampleComponent limel-example-button-group-mix
 */
@Component({
    tag: 'limel-button-group',
    shadow: true,
    styleUrl: 'button-group.scss',
})
export class ButtonGroup {
    /**
     * List of buttons for the group
     */
    @Prop()
    public value: Button[] = [];

    /**
     * True if the button-group should be disabled
     */
    @Prop({ reflect: true })
    public disabled: boolean = false;

    /**
     * Dispatched when a button is selected/deselected
     */
    @Event()
    private change: EventEmitter<Button>;

    @State()
    private selectedButtonId: string;

    private radioGroupName = createRandomString();

    constructor() {
        this.renderButton = this.renderButton.bind(this);
        this.onChange = this.onChange.bind(this);
        this.renderContent = this.renderContent.bind(this);
    }

    public componentWillLoad() {
        this.selectedButtonId = this.value.find((button) => {
            return button.selected;
        })?.id;
    }

    public render() {
        const classes = {
            'mdc-chip-set': true,
            disabled: this.disabled,
            'mdc-chip-set--choice': true,
        };

        return (
            <div class={classes} role="grid">
                {this.value.map(this.renderButton)}
            </div>
        );
    }

    private renderButton(button: Button) {
        const classes = {
            'mdc-chip': true,
            'mdc-chip--selected': this.isButtonChecked(button),
        };

        return (
            <div class={classes} role="row">
                <span role="gridcell">
                    <input
                        type="radio"
                        name={this.radioGroupName}
                        checked={this.isButtonChecked(button)}
                        id={button.id}
                        onChange={this.onChange}
                    />
                    <label htmlFor={button.id}>
                        {this.renderContent(button)}
                    </label>
                </span>
            </div>
        );
    }

    private renderContent(button: Button) {
        if (button.icon) {
            return this.renderIcon(button);
        }

        return this.renderLabel(button);
    }

    private isButtonChecked(button: Button) {
        return button.id === this.selectedButtonId;
    }

    private renderLabel(button: Button) {
        return <span class="mdc-chip__text">{button.title}</span>;
    }

    private renderIcon(button: Button) {
        return (
            <limel-icon
                class="mdc-chip__icon"
                aria-label={button.title}
                title={button.title}
                name={button.icon}
                size="small"
                badge={true}
            />
        );
    }

    private onChange(event: Event) {
        event.stopPropagation();
        const target = event.target as HTMLInputElement;
        this.selectedButtonId = target.id;
        const button = this.value.find((item) => {
            return `${item.id}` === target.id;
        });
        this.change.emit(button);
    }
}

@import '../../style/internal/mdc-variables';
@import '../../style/internal/shared_input-select-picker';

@import '@limetech/mdc-chips/mdc-chips';

@import '../../style/mixins';

.mdc-chip {
    @include is-flat-inset-clickable;
    max-width: 100%;
    min-width: pxToRem(32);
    padding: 0 pxToRem(1);
    display: inline-grid;
    grid-auto-flow: column;
    background-color: transparent;
    margin: pxToRem(2) !important;

    &:not(:last-child) {
        &:after {
            content: '';
            display: block;

            height: pxToRem(16);
            width: pxToRem(2);
            border-radius: pxToRem(4);
            background-color: rgb(var(--contrast-600));

            position: absolute;
            right: pxToRem(-3);
            top: 0;
            bottom: 0;
            margin: auto;
        }
    }

    label {
        cursor: pointer;
        transition: color 0.2s ease;
    }

    input[type='radio'] {
        width: 0;
        position: absolute;
        opacity: 0; // This removes some visual defects on iOS and Firefox, created by "user agent styles"

        &:focus-visible {
            + label {
                &:after {
                    // visualizes keyboard navigation
                    // only when non-pointer input is being used,
                    // e.g. tabbed into using keyboard
                    content: '';
                    display: block;
                    position: absolute;
                    top: 0;
                    right: 0;
                    bottom: 0;
                    left: 0;
                    border-radius: pxToRem(60);
                    box-shadow: var(--shadow-depth-8-focused);
                    z-index: $limel-button-group-radio-button-keyboard-focused--z-index;
                }
            }
        }
    }

    span[role='gridcell'] {
        min-width: 0; // This is needed to force mdc-chip__text (which is inside this span) to truncate

        &:focus-within {
            // Cannot use `:focus-visible` here, since it is the radio button
            // which gets focused, which is inside this element…
            // and we are removing or hiding the visual appearance of the button
            outline: none;
            color: var(--mdc-theme-primary);
        }

        &:only-child {
            .mdc-chip__text {
                padding-left: pxToRem(12);
            }
        }

        &:first-child {
            .mdc-chip__text {
                padding-left: pxToRem(12);
            }
        }
    }

    &:hover {
        background-color: $mdc-chip-background-color;
    }

    &.mdc-chip--selected {
        box-shadow: var(--button-shadow-inset);

        &:active {
            box-shadow: var(--button-shadow-inset-pressed);
        }
    }

    &.mdc-chip--selected {
        .mdc-chip-set--choice & {
            color: rgb(var(--contrast-1300));
        }
    }
}

.mdc-chip__text {
    overflow: hidden;
    text-overflow: ellipsis;
    display: block;
    padding: 0 pxToRem(12) 0 pxToRem(4);
}

limel-icon.mdc-chip__icon.mdc-chip__icon--leading {
    background-color: var(--icon-background-color, transparent);
    margin: 0 !important;
    color: var(--icon-color, rgba(0, 0, 0, 0.54));
}

.mdc-chip-set {
    align-items: center;
    border-radius: pxToRem(60);
    background-color: rgb(var(--contrast-400));
    padding: 0;

    width: max-content;
    max-width: 100%;

    flex-wrap: nowrap; // Not sure about this. It'll put items on one row, but also can look bad on action bars. Kia

    &.disabled {
        @include looks-disabled;
    }
}

import { MDCCheckbox } from '@limetech/mdc-checkbox';
import { MDCFormField } from '@limetech/mdc-form-field';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { CheckboxTemplate } from './checkbox.template';

/**
 * @exampleComponent limel-example-checkbox
 */
@Component({
    tag: 'limel-checkbox',
    shadow: true,
    styleUrl: 'checkbox.scss',
})
export class Checkbox {
    /**
     * Disables the checkbox when `true`. Works exactly the same as `readonly`.
     * If either property is `true`, the checkbox will be disabled.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Disables the checkbox when `true`. Works exactly the same as `disabled`.
     * If either property is `true`, the checkbox will be disabled.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * The checkbox label.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * The value of the checkbox. Set to `true` to make the checkbox checked.
     */
    @Prop({ reflect: true })
    public checked = false;

    /**
     * Set to `true` to indicate that the checkbox must be checked.
     */
    @Prop({ reflect: true })
    public required: boolean = false;

    @State()
    private modified = false;

    /**
     * Emitted when the input value is changed.
     */
    @Event()
    private change: EventEmitter<boolean>;

    @Element()
    private limelCheckbox: HTMLLimelCheckboxElement;

    private formField: MDCFormField;
    private mdcCheckbox: MDCCheckbox;
    private id: string = createRandomString();

    constructor() {
        this.onChange = this.onChange.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.limelCheckbox.shadowRoot.querySelector(
            '.mdc-form-field'
        );
        if (!element) {
            return;
        }

        this.formField = new MDCFormField(element);
        this.mdcCheckbox = new MDCCheckbox(
            this.limelCheckbox.shadowRoot.querySelector('.mdc-checkbox')
        );
        this.formField.input = this.mdcCheckbox;
    }

    public disconnectedCallback() {
        this.mdcCheckbox?.destroy();
        this.formField?.destroy();
    }

    public render() {
        return (
            <CheckboxTemplate
                disabled={this.disabled || this.readonly}
                label={this.label}
                checked={this.checked}
                required={this.required}
                invalid={this.required && this.modified && !this.checked}
                onChange={this.onChange}
                id={this.id}
            />
        );
    }

    private onChange(event: Event) {
        event.stopPropagation();
        this.change.emit(this.mdcCheckbox.checked);
        this.modified = true;
    }
}

@import '../../style/internal/mdc-variables';

@import '@limetech/mdc-form-field/mdc-form-field';
@import '@limetech/mdc-checkbox/mdc-checkbox';

.mdc-form-field {
    display: flex;

    .mdc-checkbox {
        @include mdc-checkbox-container-colors(
            secondary,
            on-primary,
            primary,
            primary
        );

        &.mdc-checkbox--invalid {
            .mdc-checkbox__native-control:enabled:not(:checked):not(:indeterminate)
                ~ .mdc-checkbox__background {
                border-color: $mdc-theme-error;
            }
        }
    }

    label {
        line-height: normal;

        &.mdc-checkbox--disabled {
            opacity: 0.37;
        }

        &.mdc-checkbox--required::after {
            margin-left: 0.0625rem;
            content: '*';
        }

        &.mdc-checkbox--invalid {
            color: $mdc-theme-error;
        }
    }
}

import { Chip, Languages } from '@limetech/lime-elements';
import {
    MDCChipInteractionEvent,
    MDCChipSelectionEvent,
    MDCChipSet,
} from '@limetech/mdc-chips';
import { MDCTextField } from '@limetech/mdc-textfield';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Method,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { handleKeyboardEvent } from './chip-set-input-helpers';
import translate from '../../global/translations';

const SELECTED_CHIP_CLASS = 'mdc-chip--selected';

/**
 * @exampleComponent limel-example-chip-set
 * @exampleComponent limel-example-chip-set-choice
 * @exampleComponent limel-example-chip-set-filter
 * @exampleComponent limel-example-chip-set-input
 * @exampleComponent limel-example-chip-icon-color
 */
@Component({
    tag: 'limel-chip-set',
    shadow: true,
    styleUrl: 'chip-set.scss',
})
export class ChipSet {
    /**
     * List of chips for the set
     */
    @Prop()
    public value: Chip[] = [];

    /**
     * Type of chip set
     *
     * - `choice` renders a set of selectable chips where only one is selectable. The `removable` property is ignored
     * - `filter` renders a set of selectable chips where all are selectable. The `icon` property is ignored
     * - `input` renders a set of chips that can be used in conjunction with an input field
     *
     * If no type is set, a basic set of chips without additional functionality will be rendered
     */
    @Prop({ reflect: true })
    public type?: 'choice' | 'filter' | 'input';

    /**
     * Label for the chip-set
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * True if the chip set should be disabled
     */
    @Prop({ reflect: true })
    public disabled: boolean = false;

    /**
     * For chip-sets of type `input`, set to `true` to disable adding and
     * removing chips, but allow interaction with existing chips in the set.
     * For any other types, setting either `readonly` or `disabled` disables
     * the chip-set.
     */
    @Prop({ reflect: true })
    public readonly: boolean = false;

    /**
     * For chip-sets of type `input`. Limits the maximum number of chips.
     * When the value is `0` or not set, no limit is applied.
     */
    @Prop({ reflect: true })
    public maxItems: number;

    /**
     * True if the control requires a value
     */
    @Prop({ reflect: true })
    public required: boolean = false;

    /**
     * Search label to display when type is `input` and component is in search mode
     */
    @Prop({ reflect: true })
    public searchLabel: string;

    /**
     * Whether the input field should be emptied when the chip-set loses focus.
     */
    @Prop({ reflect: true })
    public emptyInputOnBlur: boolean = true;

    /**
     * For chip-sets of type `input`. When the value is null, no leading icon is used.
     * Leading icon to show to the far left in the text field
     */
    @Prop({ reflect: true })
    public leadingIcon: string = null;

    /**
     * For chip-set of type `input`. Sets delimiters between chips.
     */
    @Prop({ reflect: true })
    public delimiter: string = null;

    /**
     * Defines the language for translations.
     * Will translate the translatable strings on the components. For example, the clear all chips label.
     */
    @Prop()
    public language: Languages = 'en';

    /**
     * Dispatched when a chip is interacted with
     */
    @Event()
    private interact: EventEmitter<Chip>;

    /**
     * Dispatched when a chip is selected/deselected
     */
    @Event()
    private change: EventEmitter<Chip | Chip[]>;

    /**
     * Emitted when an input chip set has received focus and editing in the text field has started
     */
    @Event()
    private startEdit: EventEmitter<void>;

    /**
     * Emitted when an input chip set has lost focus and editing in the text field has ended
     */
    @Event()
    private stopEdit: EventEmitter<void>;

    /**
     * Dispatched when the input is changed for type `input`
     */
    @Event()
    private input: EventEmitter<string>;

    @Element()
    private host: HTMLLimelChipSetElement;

    @State()
    private editMode: boolean = false;

    @State()
    private textValue: string = '';

    @State()
    private blurred: boolean = false;

    @State()
    private inputChipIndexSelected: number = null;

    private mdcChipSet: MDCChipSet;
    private mdcTextField: MDCTextField;
    private handleKeyDown = handleKeyboardEvent;
    private clearAllChipsLabel: string;

    constructor() {
        this.renderChip = this.renderChip.bind(this);
        this.renderInputChip = this.renderInputChip.bind(this);
        this.isFull = this.isFull.bind(this);
        this.handleInteractionEvent = this.handleInteractionEvent.bind(this);
        this.handleSelection = this.handleSelection.bind(this);
        this.handleRemoveEvent = this.handleRemoveEvent.bind(this);
        this.handleTextFieldFocus = this.handleTextFieldFocus.bind(this);
        this.handleInputBlur = this.handleInputBlur.bind(this);
        this.handleTextInput = this.handleTextInput.bind(this);
        this.inputFieldOnChange = this.inputFieldOnChange.bind(this);
        this.handleKeyDown = this.handleKeyDown.bind(this);
        this.inputHidden = this.inputHidden.bind(this);
        this.handleDeleteAllIconClick = this.handleDeleteAllIconClick.bind(
            this
        );
        this.renderDelimiter = this.renderDelimiter.bind(this);
    }

    /**
     * Used to find out whether the chip-set is in edit mode.
     *
     * @returns {Promise<boolean>} `true` if the chip-set is in edit mode, `false` otherwise.
     */
    @Method()
    public async getEditMode(): Promise<boolean> {
        return this.editMode;
    }

    /**
     * Used to set focus to the chip-set input field.
     *
     * @param {boolean} emptyInput if `true`, any text in the input is discarded
     *
     * @returns {Promise<void>} does not return anything, but methods have to be async
     */
    @Method()
    public async setFocus(emptyInput: boolean = false) {
        this.editMode = true;
        if (emptyInput) {
            this.textValue = '';
        }

        this.host.shadowRoot.querySelector('input').focus();
        this.startEdit.emit();
    }

    /**
     * Used to empty the input field. Used in conjunction with `emptyInputOnBlur` to let the
     * consumer control when the input is emptied.
     *
     * @returns {Promise<void>} does not return anything, but methods have to be async
     */
    @Method()
    public async emptyInput() {
        this.syncEmptyInput();
    }

    public componentWillLoad() {
        this.clearAllChipsLabel = translate.get(
            'chip-set.clear-all',
            this.language
        );
    }

    public componentDidLoad() {
        if (this.type === 'input') {
            this.mdcTextField = new MDCTextField(
                this.host.shadowRoot.querySelector('.mdc-text-field')
            );
        }

        this.createMDCChipSet();
    }

    public componentWillUpdate() {
        this.destroyMDCChipSet();
    }

    public componentDidUpdate() {
        this.createMDCChipSet();
        const input = this.host.shadowRoot.querySelector('input');
        if (input && this.editMode) {
            input.focus();
        }
    }

    public disconnectedCallback() {
        this.destroyMDCChipSet();

        if (this.mdcTextField) {
            this.mdcTextField.destroy();
        }
    }

    public render() {
        if (this.type === 'input') {
            return this.renderInputChips();
        }

        const classes = {
            'mdc-chip-set': true,
            disabled: this.disabled || this.readonly,
            'mdc-text-field--with-trailing-icon': true,
        };
        if (this.type) {
            classes[`mdc-chip-set--${this.type}`] = true;
        }

        const chipSetLabel = this.renderChipSetLabel();
        if (chipSetLabel) {
            classes['chip-set--with-label'] = true;
        }

        return (
            <div class={classes} role="grid">
                {chipSetLabel}
                {this.value.map(this.renderChip)}
            </div>
        );
    }

    @Watch('value')
    protected handleChangeChips() {
        this.syncEmptyInput();
    }

    private createMDCChipSet() {
        this.mdcChipSet = new MDCChipSet(
            this.host.shadowRoot.querySelector('.mdc-chip-set')
        );
        this.mdcChipSet.chips.forEach((chip) => {
            chip.shouldRemoveOnTrailingIconClick = false;
        });

        if (!this.type || this.type === 'input') {
            this.mdcChipSet.listen(
                'MDCChip:interaction',
                this.handleInteractionEvent
            );
        }

        if (this.type === 'choice' || this.type === 'filter') {
            this.mdcChipSet.listen('MDCChip:selection', this.handleSelection);
        }

        this.mdcChipSet.listen(
            'MDCChip:trailingIconInteraction',
            this.handleRemoveEvent
        );
    }

    private destroyMDCChipSet() {
        if (this.mdcChipSet) {
            this.mdcChipSet.unlisten(
                'MDCChip:interaction',
                this.handleInteractionEvent
            );
            this.mdcChipSet.unlisten('MDCChip:selection', this.handleSelection);
            this.mdcChipSet.unlisten(
                'MDCChip:trailingIconInteraction',
                this.handleRemoveEvent
            );

            this.mdcChipSet.destroy();
        }
    }

    private renderChipSetLabel() {
        if (!this.label) {
            return;
        }

        return (
            <label class="chip-set__label mdc-floating-label lime-floating-label--float-above">
                {this.label}
            </label>
        );
    }

    private renderInputChips() {
        return (
            <div
                class={{
                    'mdc-text-field mdc-text-field--outlined': true,
                    'mdc-chip-set mdc-chip-set--input': true,
                    'force-invalid': this.isInvalid(),
                    'mdc-text-field--disabled': this.readonly || this.disabled,
                    'has-chips': this.value.length !== 0,
                    'has-leading-icon': this.leadingIcon !== null,
                }}
                onClick={this.handleTextFieldFocus}
            >
                {this.value.map(this.renderInputChip)}
                <input
                    type="text"
                    id="input-element"
                    disabled={this.readonly || this.disabled}
                    class={{
                        'mdc-text-field__input': true,
                        hidden: this.inputHidden(),
                    }}
                    value={this.textValue}
                    onBlur={this.handleInputBlur}
                    onFocus={this.handleTextFieldFocus}
                    onKeyDown={this.handleKeyDown}
                    onInput={this.handleTextInput}
                    // Some browsers emit a change event on input elements, we need to stop
                    // that event from propagating since we are emitting our own change event
                    onChange={this.inputFieldOnChange}
                    placeholder={this.isFull() ? '' : this.searchLabel}
                    readonly={this.isFull()}
                />
                <div
                    class={{
                        'mdc-notched-outline': true,
                        'lime-notched-outline--notched': !!this.value.length,
                    }}
                >
                    <div class="mdc-notched-outline__leading"></div>
                    <div class="mdc-notched-outline__notch">
                        <label
                            class={{
                                'mdc-floating-label': true,
                                'mdc-text-field--disabled':
                                    this.readonly || this.disabled,
                                'mdc-text-field--required': this.required,
                                'lime-floating-label--float-above': !!(
                                    this.value.length || this.editMode
                                ),
                            }}
                            htmlFor="input-element"
                        >
                            {this.label}
                        </label>
                    </div>
                    <div class="mdc-notched-outline__trailing"></div>
                </div>
                {this.renderLeadingIcon()}
                {this.renderClearAllChipsButton()}
            </div>
        );
    }

    private isFull(): boolean {
        return !!this.maxItems && this.value.length >= this.maxItems;
    }

    private isInvalid() {
        if (!this.required) {
            return false;
        }

        if (!this.blurred) {
            return false;
        }

        return !this.value || !this.value.length;
    }

    private inputFieldOnChange(event) {
        event.stopPropagation();
    }

    /**
     * Enter edit mode when the text field receives focus. When editMode is true, the input element will be visible
     *
     * @returns {void}
     */
    private handleTextFieldFocus() {
        if (this.editMode) {
            return;
        }

        this.editMode = true;
        this.startEdit.emit();
    }

    /**
     * Exit edit mode when the input element loses focus. This makes sure the input element does not take up any
     * additional space when the user it not typing anything
     *
     * @returns {void}
     */
    private handleInputBlur() {
        if (this.emptyInputOnBlur) {
            this.syncEmptyInput();
        }

        this.editMode = false;
        this.blurred = true;
        this.inputChipIndexSelected = null;

        // This timeout is needed in order to let a new element receive focus
        setTimeout(() => {
            this.stopEdit.emit();
        }, 0);
    }

    private syncEmptyInput() {
        this.textValue = '';
    }

    private inputHidden() {
        if (this.editMode) {
            return this.isFull();
        }

        // If there are chips in the picker, hide the input to avoid the input
        // being placed on a new line and adding ugly space beneath the chips.
        // If there are no chips, show the input, or the picker will look weird.
        return !!(this.value && this.value.length);
    }

    private handleTextInput(event) {
        event.stopPropagation();
        this.inputChipIndexSelected = null;
        this.textValue = event.target.value;
        this.input.emit(event.target.value && event.target.value.trim());
    }

    private handleInteractionEvent(event: MDCChipInteractionEvent) {
        const chip = this.value.find((item) => {
            return `${item.id}` === event.detail.chipId;
        });
        this.emitInteraction(chip);
    }

    private emitInteraction(chip: Chip) {
        this.interact.emit(chip);
    }

    private handleSelection(event: MDCChipSelectionEvent) {
        let chip = this.value.find((item) => {
            return `${item.id}` === event.detail.chipId;
        });
        chip = { ...chip, selected: event.detail.selected };
        this.change.emit(chip);
    }

    private handleRemoveEvent(event: MDCChipInteractionEvent) {
        this.removeChip(event.detail.chipId);
    }

    private removeChip(id: string | number) {
        const newValue = this.value.filter((chip) => {
            return `${chip.id}` !== `${id}`;
        });
        this.change.emit(newValue);
    }

    private renderChip(chip: Chip) {
        if (this.type === 'filter') {
            return this.renderFilterChip(chip);
        }

        return this.renderDefaultChip(chip);
    }

    private renderDefaultChip(chip: Chip) {
        return (
            <div
                class={`mdc-chip ${chip.selected ? SELECTED_CHIP_CLASS : ''}`}
                role="row"
                id={`${chip.id}`}
            >
                {chip.icon ? this.renderIcon(chip) : null}
                {chip.text ? this.renderLabel(chip) : null}
            </div>
        );
    }

    private renderLabel(chip: Chip<any>) {
        return (
            <span role="gridcell">
                <span role="button" tabindex="0" class="mdc-chip__text">
                    {chip.text}
                </span>
            </span>
        );
    }

    private renderFilterChip(chip: Chip) {
        return (
            <div
                class={`mdc-chip ${chip.selected ? SELECTED_CHIP_CLASS : ''}`}
                role="row"
                id={`${chip.id}`}
            >
                <span class="mdc-chip__checkmark">
                    <svg class="mdc-chip__checkmark-svg" viewBox="-2 -3 30 30">
                        <path
                            class="mdc-chip__checkmark-path"
                            fill="none"
                            stroke="black"
                            d="M1.73,12.91 8.1,19.28 22.79,4.59"
                        />
                    </svg>
                </span>
                <span role="gridcell">
                    <span
                        role="checkbox"
                        tabindex="0"
                        aria-checked="false"
                        class="mdc-chip__text"
                    >
                        {chip.text}
                    </span>
                </span>
            </div>
        );
    }

    private renderInputChip(chip: Chip, index: number) {
        const attributes: { tabindex?: number } = {};
        if (this.readonly && !this.disabled) {
            attributes.tabindex = 0;
        }

        return [
            this.renderDelimiter(index),
            <div
                class={{
                    'mdc-chip': true,
                    'mdc-chip--selected': this.inputChipIndexSelected === index,
                    disabled: this.disabled,
                }}
                role="row"
                id={`${chip.id}`}
                onClick={this.catchInputChipClicks}
                {...attributes}
            >
                {chip.icon ? this.renderIcon(chip) : null}
                {this.renderLabel(chip)}
                {this.renderChipRemoveButton(chip)}
            </div>,
        ];
    }

    private catchInputChipClicks(event) {
        event.stopPropagation();
    }

    private renderIcon(chip: Chip) {
        const style = {};
        if (chip.iconFillColor) {
            style['--icon-color'] = chip.iconFillColor;
        }

        if (chip.iconBackgroundColor) {
            style['--icon-background-color'] = chip.iconBackgroundColor;
        }

        if (chip.iconColor) {
            // eslint-disable-next-line no-console
            console.warn(
                '`Chip.iconColor` is deprecated. Use `Chip.iconBackgroundColor` to set the background color of the icon.'
            );
            style['--icon-background-color'] = chip.iconColor;
        }

        return (
            <limel-icon
                class="mdc-chip__icon mdc-chip__icon--leading"
                name={chip.icon}
                style={style}
                size="small"
                badge={true}
            />
        );
    }

    private renderLeadingIcon() {
        if (!this.leadingIcon) {
            return;
        }

        return (
            <i class="mdc-text-field__icon search-icon">
                <limel-icon name={this.leadingIcon} />
            </i>
        );
    }

    private renderChipRemoveButton(chip: Chip) {
        if (!chip.removable || this.readonly || this.disabled) {
            return;
        }

        const svgData = `<svg style="height:100%;width:100%;" width="32" height="32" x="0px" y="0px" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg" xmlns:svg="http://www.w3.org/2000/svg">
    <line fill="none" id="svg_1" stroke="currentColor" stroke-width="2" x1="8" x2="24" y1="8" y2="24"/>
    <line fill="none" id="svg_2" stroke="currentColor" stroke-width="2" x1="24" x2="8" y1="8" y2="24"/>
</svg>`;

        return (
            <div
                class="mdc-chip__icon mdc-chip__icon--trailing"
                role="button"
                innerHTML={svgData}
            />
        );
    }

    private renderClearAllChipsButton() {
        return (
            <a
                href=""
                onClick={this.handleDeleteAllIconClick}
                class="mdc-text-field__icon clear-all-button"
                tabindex="0"
                role="button"
                title={this.clearAllChipsLabel}
                aria-label={this.clearAllChipsLabel}
            />
        );
    }
    private handleDeleteAllIconClick(event: Event) {
        event.preventDefault();
        this.change.emit([]);
    }

    private renderDelimiter(index: Number) {
        if (index === 0 || !this.delimiter) {
            return;
        }

        return <div class="delimiter">{this.delimiter}</div>;
    }
}

@import '../../style/internal/mdc-variables';
@import '../input-field/input-field';
@import '../../style/internal/shared_input-select-picker';

@import '@limetech/mdc-chips/mdc-chips';

@import '../../style/mixins';

/**
 * @prop --icon-background-color: Background color of the icon. Defaults to transparent.
 * @prop --icon-color: Color of the icon. Defaults to `rgb(var(--contrast-1100))`.
 * @prop --background-color: Background color of the field when type is set to input.
 * @prop --background-color-disabled: Background color of the field when type is set to input and the component is disabled or readonly.
 * @prop --input-chip-set-selected-chip-color: Color of the highlight around selected chips in input chip-sets.
 */
$height-of-chip-set-input: pxToRem(36);

.mdc-chip {
    @include is-elevated-inset-clickable;
    max-width: 100%;
    min-width: pxToRem(32);
    padding: 0 pxToRem(1);
    display: inline-grid;
    grid-auto-flow: column;
    background-color: $mdc-chip-background-color;

    span[role='gridcell'] {
        min-width: 0; // This is needed to force mdc-chip__text (which is inside this span) to truncate

        &:only-child {
            .mdc-chip__text {
                padding-left: pxToRem(12);
            }
        }

        &:first-child {
            .mdc-chip__text {
                padding-left: pxToRem(12);
            }
        }
    }

    &:hover {
        background-color: $mdc-chip-background-color;
    }

    &.mdc-chip--selected {
        box-shadow: var(--button-shadow-inset);

        &:active {
            box-shadow: var(--button-shadow-inset-pressed);
        }
    }
}

.mdc-chip.mdc-chip--selected .mdc-chip__checkmark,
.mdc-chip__checkmark {
    margin-right: pxToRem(4);
    margin-left: pxToRem(8);
}

.mdc-chip__text {
    overflow: hidden;
    text-overflow: ellipsis;
    display: block;
    padding: 0 pxToRem(12) 0 pxToRem(4);
}

limel-icon.mdc-chip__icon.mdc-chip__icon--leading {
    background-color: var(--icon-background-color, transparent);
    margin: 0 !important;
    color: var(--icon-color, rgb(var(--contrast-1100)));
}

.mdc-chip-set {
    align-items: center;
    min-height: $height-of-mdc-text-field;
    position: relative;

    &.chip-set--with-label {
        .lime-floating-label--float-above {
            padding-left: pxToRem(4);
        }
    }

    &.mdc-chip-set--input {
        padding: pxToRem(8);
        padding-right: pxToRem(
            32
        ); // This leaves space for "clear all" button and avoids overlapping with chips
        width: 100%;

        .mdc-chip--selected {
            // When chip is selected with keyboard (backspace / arrow-keys) to be deleted
            box-shadow: 0 0 0 pxToRem(3) var(--lime-deep-red);

            .mdc-chip__icon--trailing {
                color: var(--lime-deep-red);
            }
        }
    }

    &.disabled:not(.mdc-chip-set--input) {
        @include looks-disabled;
    }
}

.mdc-chip__icon {
    &.mdc-chip__icon--trailing {
        transition: background-color 0.2s ease;

        margin-left: 0;
        margin-right: pxToRem(5);

        width: pxToRem(22);
        height: pxToRem(22);

        &:hover {
            background-color: rgba(var(--color-red-default), 0.2);

            svg {
                transform: scale(0.7);
            }
        }

        svg {
            transition: transform 0.2s ease;
            display: block;
            transform: scale(0.9);
        }
    }
}

.mdc-text-field {
    @include mdc-states-hover-opacity(0);
    @include mdc-states-focus-opacity(0, true);

    width: 100%;
    height: auto;
    cursor: text;

    .mdc-text-field__input {
        width: auto;
        height: $height-of-chip-set-input;
        line-height: $height-of-chip-set-input;
        padding: 0 pxToRem(12);

        flex-grow: 1;
        flex-shrink: 0;

        &.hidden {
            // This class is added, as soon as there is a chip selected and displayed
            // This input field should not be visually visible as it breaks the UI in some cases
            // But it should be rendered to be able to tab between fields and do other keyboard commands /Kia
            transition: all 0s;
            opacity: 0;
            position: absolute;
            z-index: $limel-chip-set--hidden-text-field--z-index; // to let users interact with chips, in case they're covered
        }
    }
}

.mdc-text-field--disabled .mdc-chip {
    pointer-events: all;

    &.disabled {
        @include looks-disabled;
    }
}

.mdc-floating-label {
    @include looks-like-input-label;
    &.mdc-text-field--required::after {
        content: '*';
    }
    &.lime-floating-label--float-above,
    &.lime-floating-label--float-above.mdc-floating-label--float-above {
        @include label--float-above;
        top: $floating-label-top-value;
    }
}

.chip-set__label {
    @include looks-like-input-label;
    padding-left: pxToRem(20);
    font-size: pxToRem(16);
}

.lime-notched-outline--notched {
    .mdc-notched-outline__notch {
        border-top: 1px solid transparent !important;
    }
}

.force-invalid {
    @extend .mdc-text-field--invalid;
}

.clear-all-button {
    transition: opacity 0.3s ease;
    @include clear-all-button;

    position: absolute;
    right: pxToRem(8);
    top: pxToRem(18);

    opacity: 0; // Is hidden, but can receive focus (such as when navigating through tab indexes).

    &:focus,
    .has-chips:not(.mdc-text-field--disabled):hover &,
    .has-chips:not(.mdc-text-field--disabled).mdc-text-field--focused & {
        opacity: 1;
    }

    :not(.has-chips) &,
    .has-chips.mdc-text-field--disabled & {
        display: none; // Won't receive focus when disabled
    }
}

.has-leading-icon {
    &:not(.has-chips) {
        .mdc-text-field__input {
            padding-left: pxToRem(40);
        }

        .mdc-floating-label {
            &:not(.lime-floating-label--float-above) {
                padding-left: pxToRem(40);
            }
        }
    }

    .mdc-chip {
        &:first-of-type {
            margin-left: 40px;
        }
    }

    .search-icon {
        transition: transform 0.2s ease;
        position: absolute;
        top: pxToRem(16);
        left: pxToRem(16);
    }

    limel-icon {
        background-color: transparent;
    }
}

.delimiter {
    opacity: 0.5;
    padding: 0 pxToRem(2);
}

import { Component, Event, EventEmitter, h, Prop } from '@stencil/core';
import { dispatchResizeEvent } from '../../util/dispatch-resize-event';
import { Action } from './action';

/**
 * @slot - Content to put inside the collapsible section
 * @exampleComponent limel-example-collapsible-section
 * @exampleComponent limel-example-collapsible-section-actions
 * @exampleComponent limel-example-collapsible-section-css-props
 * @exampleComponent limel-example-collapsible-section-external-control
 * @exampleComponent limel-example-collapsible-section-with-slider
 */
@Component({
    tag: 'limel-collapsible-section',
    shadow: true,
    styleUrl: 'collapsible-section.scss',
})
export class CollapsibleSection {
    /**
     * `true` if the section is expanded, `false` if collapsed.
     */
    @Prop({ mutable: true, reflect: true })
    public isOpen: boolean = false;

    /**
     * Text to display in the header of the section
     */
    @Prop()
    public header: string;

    /**
     * Actions to place to the far right inside the header
     */
    @Prop()
    public actions: Action[];

    /**
     * Emitted when the section is expanded
     */
    @Event()
    private open: EventEmitter<void>;

    /**
     * Emitted when the section is collapsed
     */
    @Event()
    private close: EventEmitter<void>;

    /**
     * Emitted when an action is clicked inside the header
     */
    @Event()
    private action: EventEmitter<Action>;

    constructor() {
        this.onClick = this.onClick.bind(this);
        this.renderActionButton = this.renderActionButton.bind(this);
    }

    public render() {
        return (
            <section class={`${this.isOpen ? 'open' : ''}`}>
                <header class="section__header" onClick={this.onClick}>
                    <div class="section__header__expand-icon">
                        <div class="expand-icon__line"></div>
                        <div class="expand-icon__line"></div>
                        <div class="expand-icon__line"></div>
                        <div class="expand-icon__line"></div>
                    </div>
                    <h2 class="section__header__title mdc-typography mdc-typography--headline2">
                        {this.header}
                    </h2>
                    <div class="section__header__divider-line" />
                    {this.renderActions()}
                </header>
                <div class="section__body">
                    <slot />
                </div>
            </section>
        );
    }

    private onClick() {
        this.isOpen = !this.isOpen;

        if (this.isOpen) {
            this.open.emit();
            const waitForUiToRender = 100;
            setTimeout(dispatchResizeEvent, waitForUiToRender);
        } else {
            this.close.emit();
        }
    }

    private renderActions() {
        if (!this.actions) {
            return;
        }

        return (
            <div class="section__header__actions">
                {this.actions.map(this.renderActionButton)}
            </div>
        );
    }

    private renderActionButton(action: Action) {
        return (
            <limel-icon-button
                icon={action.icon}
                label={action.label}
                disabled={action.disabled}
                onClick={this.handleActionClick(action)}
            />
        );
    }

    private handleActionClick = (action: Action) => (event: MouseEvent) => {
        event.stopPropagation();
        this.action.emit(action);
    };
}

@import '../../style/internal/mdc-variables';
@import '../../style/mixins';

/**
 * @prop --closed-header-background-color: background color for header when closed
 * @prop --open-header-background-color: background color for header when open
 * @prop --body-background-color: background color for body
 * @prop --body-padding: space around content of the body
 */

:host {
    --border-radius-of-header: 0.75rem;
    display: block;
}

:host([hidden]) {
    display: none;
}

:host([disabled]) {
    pointer-events: none;
}

.section__header {
    transition: background-color 0.4s ease, border-radius 0.1s ease;
    cursor: pointer;

    align-items: center;
    display: flex;
    justify-content: space-between;

    padding-left: pxToRem(10);
    padding-right: pxToRem(10);
    height: pxToRem(60);

    background-color: var(--closed-header-background-color, #fafafb);
    border-radius: var(--border-radius-of-header);

    section.open & {
        background-color: var(--open-header-background-color, #ffffff);
        border-radius: var(--border-radius-of-header)
            var(--border-radius-of-header) 0 0;
        &:hover {
            background-color: var(--open-header-background-color, #f6f6f7);
        }
    }
    &:hover {
        background-color: var(--open-header-background-color, #f6f6f7);
    }
}

.section__header__title {
    @include truncate-text;
    line-height: normal;

    justify-self: flex-start;
    padding-right: pxToRem(12);

    user-select: none; // mostly to improve experience on Android, where tapping on sections selects the text too
}

.section__header__divider-line {
    transition: opacity 0.3s ease 0.3s;
    flex-grow: 1;
    height: pxToRem(2);
    border-radius: pxToRem(1);
