        if (this.value?.filename?.substr(-5) === '.crab') {
            this.value.icon = 'crab';
            this.value.iconColor = 'white';
            this.value.iconBackgroundColor = 'pink';
        }
    }

    private toggleRequired() {
        this.required = !this.required;
    }
