        this.align = event.detail;
    }

    private justifyOnChange(event: CustomEvent<Option<FlexContainerJustify>>) {
        this.justify = event.detail;
    }

    private reverseOnChange(event: CustomEvent<boolean>) {
        this.reverse = event.detail;
    }
}

