        return this.colors.map((color) => {
            return [
                <div class={`swatch --color-${color}-light`}></div>,
                <div class={`swatch --color-${color}-default`}>·</div>,
                <div class="swatch hue">{color}</div>,
            ];
        });
    }

    private toggleMode() {
        this.brandColors = !this.brandColors;
    }
}

