import { Chip, FileInfo } from '@limetech/lime-elements';
import { MDCTextField } from '@limetech/mdc-textfield';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';

const CHIP_SET_TAG_NAME = 'limel-chip-set';
const DEFAULT_FILE_CHIP: Chip = {
    id: null,
    text: null,
    removable: true,
};
const DEFAULT_ICON = 'note';

/**
 * This component lets end-users select a *single* file from their device
 * storage. Regardless of the user's device or operating system, this component
 * opens up a file picker dialog that allows the user to choose a file.
 *
 * ## Using correct labels
 *
 * This file picker can be used in different contexts. The component's distinct
 * visual design including the upload icon hints end-users that this is not a
 * normal input field like other fields in the form for example.
 *
 *:::important
 * you need to use a descriptive `label` that clarifies the
 * functionality of the file picker, and/or provides users with clear
 * instructions.
 *
 * Depending on the context, you may need to avoid labels such as:
 * - File
 * - Document
 *
 * and instead consider using labels like:
 * - Attach a file
 * - Upload a file
 * - Choose a document
 * - Choose a file
 *
 * and similar phrases...
 *:::
 *
 * @exampleComponent limel-example-file
 * @exampleComponent limel-example-file-custom-icon
 * @exampleComponent limel-example-file-accepted-types
 */
@Component({
    tag: 'limel-file',
    shadow: true,
    styleUrl: 'file.scss',
})
export class File {
    /**
     * The selected file.
     */
    @Prop()
    public value: FileInfo;

    /**
     * The input label.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Set to `true` to indicate that the field is required.
     */
    @Prop({ reflect: true })
    public required: boolean = false;

    /**
     * True if the input should be disabled
     */
    @Prop({ reflect: true })
    public disabled: boolean = false;

    /**
     * Set to `true` to disable adding and removing files, but allow interaction
     * with any already existing file.
     */
    @Prop({ reflect: true })
    public readonly: boolean = false;

    /**
     * The [accepted file types](https://developer.mozilla.org/en-US/docs/Web/HTML/Element/input/file#unique_file_type_specifiers)
     */
    @Prop({ reflect: true })
    public accept: string = '*';

    /**
     * Dispatched when a file is selected/deselected
     */
    @Event()
    private change: EventEmitter<FileInfo>;

    /**
     * Dispatched when clicking on a chip
     */
    @Event()
    private interact: EventEmitter<number | string>;

    @Element()
    private element: HTMLLimelFileElement;

    private fileInput: HTMLInputElement;
    private fileInputId = createRandomString();
    private chipSet;
    private mdcTextField;

    constructor() {
        this.handleFileSelection = this.handleFileSelection.bind(this);
        this.handleFileChange = this.handleFileChange.bind(this);
        this.handleChipSetChange = this.handleChipSetChange.bind(this);
        this.handleFileDrop = this.handleFileDrop.bind(this);
        this.handleKeyDown = this.handleKeyDown.bind(this);
        this.handleKeyUp = this.handleKeyUp.bind(this);
        this.handleChipInteract = this.handleChipInteract.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.fileInput = this.element.shadowRoot.getElementById(
            this.fileInputId
        ) as HTMLInputElement;
        this.chipSet = this.element.shadowRoot.querySelector(CHIP_SET_TAG_NAME);
        this.initialize();
    }

    private initialize() {
        if (!this.chipSet) {
            return;
        }

        this.mdcTextField = new MDCTextField(
            this.chipSet.shadowRoot.querySelector('.mdc-text-field')
        );
    }

    public disconnectedCallback() {
        if (this.mdcTextField) {
            this.mdcTextField.destroy();
        }
    }

    public render() {
        return [
            <input
                hidden={true}
                id={this.fileInputId}
                onChange={this.handleFileChange}
                type="file"
                accept={this.accept}
                disabled={this.disabled || this.readonly}
            />,
            <limel-chip-set
                disabled={this.disabled}
                readonly={this.readonly}
                label={this.label}
                leadingIcon="upload_to_cloud"
                onChange={this.handleChipSetChange}
                onClick={this.handleFileSelection}
                onDragEnter={this.preventAndStop}
                onDragOver={this.preventAndStop}
                onDrop={this.handleFileDrop}
                onInteract={this.handleChipInteract}
                onKeyDown={this.handleKeyDown}
                onKeyUp={this.handleKeyUp}
                required={this.required}
                type="input"
                value={this.chipArray}
            />,
        ];
    }

    private get chipArray() {
        if (!this.value) {
            return [];
        }

        return [
            {
                ...DEFAULT_FILE_CHIP,
                text: this.value.filename,
                id: this.value.id,
                icon: this.value.icon || DEFAULT_ICON,
                iconFillColor: this.value.iconColor,
                iconBackgroundColor: this.value.iconBackgroundColor,
            },
        ];
    }

    private handleKeyDown(event: KeyboardEvent) {
        if (
            event.code === 'Tab' ||
            event.code === 'Backspace' ||
            event.code === 'Enter'
        ) {
            return;
        }

        event.preventDefault();
        event.stopPropagation();
    }

    private handleKeyUp(event: KeyboardEvent) {
        if (event.code === 'Enter' && !this.value) {
            this.fileInput.click();
        }
    }

    private handleFileSelection(event: Event) {
        event.stopPropagation();
        event.preventDefault();
        if (!this.value) {
            this.fileInput.click();
        }
    }

    private handleFileChange(event: Event) {
        if (this.fileInput.files.length > 0) {
            event.stopPropagation();
            this.handleFile(this.fileInput.files[0]);
        }
    }

    private handleFile(file) {
        const limeFile: FileInfo = {
            id: createRandomString(),
            filename: file.name,
            contentType: file.type,
            size: file.size,
            fileContent: file,
        };
        this.change.emit(limeFile);
        this.chipSet.blur();
        this.mdcTextField.valid = true;
    }

    private handleChipSetChange(event: CustomEvent) {
        event.stopPropagation();
        const file = !event.detail.length ? event.detail[0] : null;
        this.chipSet.blur();
        if (!file) {
            this.fileInput.value = '';
            this.change.emit(file);
            if (this.required) {
                this.mdcTextField.valid = false;
            }
        }
    }

    private handleFileDrop(event: DragEvent) {
        this.preventAndStop(event);
        const dataTransfer = event.dataTransfer;
        this.handleFile(dataTransfer.files[0]);
    }

    private handleChipInteract(event: CustomEvent<Chip>) {
        event.stopPropagation();
        event.preventDefault();
        this.interact.emit(event.detail.id);
    }

    private preventAndStop(event: Event) {
        event.stopPropagation();
        event.preventDefault();
    }
}

/**
 * @prop --icon-background-color: Background color of the icon. Defaults to `var(--lime-light-grey)``.
 * @prop --icon-color: Color of the icon. Defaults to 54% black.
 */

:host {
    --icon-background-color: var(--lime-light-grey);
}

import { Component, Event, EventEmitter, h, Prop } from '@stencil/core';
import { DateType, Languages } from '@limetech/lime-elements';
import translate from '../../../global/translations';
import { DatePicker as DateOnlyPicker } from '../pickers/DatePicker';
import { DatetimePicker } from '../pickers/DatetimePicker';
import { MonthPicker } from '../pickers/MonthPicker';
import { Picker } from '../pickers/Picker';
import { QuarterPicker } from '../pickers/QuarterPicker';
import { TimePicker } from '../pickers/TimePicker';
import { WeekPicker } from '../pickers/WeekPicker';
import { YearPicker } from '../pickers/YearPicker';

/**
 * This component is internal and only supposed to be used by
 * the limel-date-picker. This component is needed in order for us
 * to render the flatpickr calendar in a portal.
 *
 * @private
 */
@Component({
    tag: 'limel-flatpickr-adapter',
    shadow: true,
    styleUrl: 'flatpickr-adapter.scss',
})
export class DatePickerCalendar {
    /**
     * The value of the field.
     */
    @Prop()
    public value: Date;

    /**
     * Type of date picker.
     */
    @Prop()
    public type: DateType = 'datetime';

    /**
     * Format to display the selected date in.
     */
    @Prop()
    public format: string;

    /**
     * Set to `true` if the calendar should be open.
     */
    @Prop()
    public isOpen: boolean;

    /**
     * The native input element to use with flatpickr.
     */
    @Prop()
    public inputElement: HTMLElement;

    /**
     * Defines the localisation for translations and date formatting.
     * Property `format` customizes the localized date format.
     */
    @Prop()
    public language: Languages = 'en';

    /**
     * Emitted when the date picker value is changed.
     */
    @Event()
    public change: EventEmitter<Date>;

    private picker: Picker;
    private flatPickrCreated: boolean = false;

    private container: HTMLElement;

    public componentWillLoad() {
        switch (this.type) {
            case 'date':
                this.picker = new DateOnlyPicker(
                    this.format,
                    this.language,
                    this.change
                );
                break;

            case 'time':
                this.picker = new TimePicker(
                    this.format,
                    this.language,
                    this.change
                );
                break;

            case 'week':
                this.picker = new WeekPicker(
                    this.format,
                    this.language,
                    this.change
                );
                break;

            case 'month':
                this.picker = new MonthPicker(
                    this.format,
                    this.language,
                    this.change,
                    translate
                );
                break;

            case 'quarter':
                this.picker = new QuarterPicker(
                    this.format,
                    this.language,
                    this.change,
                    translate
                );
                break;
            case 'year':
                this.picker = new YearPicker(
                    this.format,
                    this.language,
                    this.change,
                    translate
                );
                break;

            case 'datetime':
            default:
                this.picker = new DatetimePicker(
                    this.format,
                    this.language,
                    this.change
                );
                break;
        }
    }

    public componentDidUpdate() {
        if (this.flatPickrCreated) {
            this.redrawFlatpickr();
        } else {
            this.createFlatpickr();
        }

        this.tryFixConfusingWidthBug();
    }

    private tryFixConfusingWidthBug() {
        // Sometimes the datepickr renders with the width set to 1px.
        // We've not been able to understand why but believe it has
        // to do with the internal implementation of flatpickr.
        // The fix below is an ugly fix that seems to solve this
        // issue for us.
        const ONE_SECOND = 1000;
        const TEN_PIXELS = 10;
        if (this.isOpen) {
            setTimeout(() => {
                if (this.isOpen) {
                    const flatpickrElement = this.container.querySelector(
                        'div.flatpickr-calendar'
                    );
                    const { width } = flatpickrElement.getBoundingClientRect();
                    if (width < TEN_PIXELS) {
                        this.destroyFlatpickr();
                        this.createFlatpickr();
                    }
                }
            }, ONE_SECOND);
        }
    }

    private createFlatpickr() {
        if (!this.inputElement) {
            // The input element is programatically set on this element
            // after its been rendered.
            return;
        }

        this.picker.init(this.inputElement, this.container, this.value);
        this.flatPickrCreated = true;
    }

    private redrawFlatpickr() {
        this.picker.redraw();
    }

    private destroyFlatpickr() {
        this.picker.destroy();
    }

    public disconnectedCallback() {
        this.picker.destroy();
    }

    public render() {
        return <div class="container" ref={(el) => (this.container = el)} />;
    }
}

@import '../../../style/internal/mdc-variables';

@import '@limetech/mdc-theme/mdc-theme';
@import '../../../../node_modules/flatpickr/dist/flatpickr.min.css';

$inactiveTextColor: var(
    --mdc-theme-text-disabled-on-background,
    #{$mdc-theme-text-disabled-on-background}
);

$datepickerHeaderColor: var(--lime-primary-color, #{$mdc-theme-primary});
$datepickerSelectedColor: $datepickerHeaderColor;
$datepickerHoverHighlightColor: $datepickerHeaderColor;
$datepickerTextOnThemeColor: var(
    --lime-on-primary-color,
    #{$mdc-theme-on-primary}
);

.container {
    display: inline-block;
    position: relative;
    width: 100%;
}

.flatpickr-calendar {
    left: 0 !important;
    top: auto !important;
    margin-top: 2px;

    .datepicker-months-container,
    .datepicker-quarters-container,
    .datepicker-weeks-container,
    .datepicker-years-container {
        display: flex;
        flex-direction: row;
        justify-content: space-around;
        flex-wrap: wrap;

        .selected {
            z-index: $limel-date-picker--flatpickr-adapter--datepicker-x-container--selected--z-index;
        }

        :hover {
            z-index: $limel-date-picker--flatpickr-adapter--datepicker-x-container--hover--z-index;
        }
    }

    .datepicker-month,
    .datepicker-quarter,
    .datepicker-week,
    .datepicker-year {
        cursor: pointer;
        display: inline-block;
        flex-grow: 0;
        padding: pxToRem(10);
        position: relative;
        min-width: pxToRem(40);

        &:after {
            border-radius: pxToRem(15);
            content: ' ';
            height: pxToRem(30);
            left: pxToRem(5);
            position: absolute;
            right: pxToRem(5);
            top: pxToRem(7);
            bottom: pxToRem(7);
            z-index: $limel-date-picker--flatpickr-adapter--datepicker-x--after--z-index;
        }

        &.selected {
            color: $datepickerTextOnThemeColor;

            &:after {
                background: $datepickerSelectedColor;
            }
        }

        &:hover {
            color: $datepickerTextOnThemeColor;

            &:after {
                background: $datepickerHoverHighlightColor;
                opacity: 0.5;
            }
        }

        .datepicker-month-in-quarter {
            color: $inactiveTextColor;
            display: block;
            font-size: pxToRem(12);
        }
    }

    .datepicker-month {
        min-width: pxToRem(70);

        &:after {
            left: pxToRem(10);
            right: pxToRem(10);
        }
    }

    .flatpickr-month {
        color: $datepickerTextOnThemeColor;

        .numInputWrapper span.arrowUp,
        .numInputWrapper span.arrowDown {
            &:after {
                border-top-color: $datepickerTextOnThemeColor;
                border-bottom-color: $datepickerTextOnThemeColor;
            }
        }
    }

    .flatpickr-months {
        background: $datepickerHeaderColor;
        color: $datepickerTextOnThemeColor;
        height: pxToRem(42);
        padding-top: pxToRem(6);
    }

    .flatpickr-prev-month,
    .flatpickr-next-month {
        // Arrow colors
        fill: $datepickerTextOnThemeColor;
        top: pxToRem(6);
        &:hover {
            color: $datepickerTextOnThemeColor;
            fill: $datepickerTextOnThemeColor;
        }
    }

    .flatpickr-current-month {
        div.flatpickr-month {
            font-size: pxToRem(16);
            font-weight: 400 !important;
            color: $datepickerTextOnThemeColor;
        }

        select {
            color: $datepickerTextOnThemeColor;

            option {
                color: initial;
            }
        }

        input {
            color: $datepickerTextOnThemeColor;
        }

        .numInputWrapper:hover {
            background: $datepickerHeaderColor;
        }
    }

    .flatpickr-weekday {
        background: $datepickerHeaderColor;
        color: $datepickerTextOnThemeColor;
        font-weight: 400;
    }

    .flatpickr-weekdays {
        background: $datepickerHeaderColor;
    }

    // Hide the arrow between the input box and the calendar
    &.arrowTop:before,
    &.arrowTop:after {
        display: none;
    }

    .flatpickr-day {
        border: none;
        background: transparent !important;
        border-radius: 0 !important;
        box-shadow: none !important;
        max-width: none;

        &.selected,
        &.inRange,
        &.selected:hover,
        &.nextMonthDay:hover,
        &:hover {
            background: transparent;
            color: $datepickerTextOnThemeColor;
        }
        &.today {
            background: transparent;
            &:after {
                background: var(
                    --mdc-theme-text-hint-on-light,
                    $mdc-theme-text-hint-on-light
                );
                opacity: 0.25;
            }
            &:hover {
                background: transparent;
                color: $datepickerTextOnThemeColor;
            }
        }

        // The circle around the date should be smaller than
        // the original, but it's hard to actually style
        // the original one and still keep some of the space
        // between the days from our design.
        // So the original circle is hidden and a new one
        // is created using a pseudo element.
        &:after {
            border-radius: pxToRem(15);
            content: ' ';
            height: pxToRem(30);
            left: 50%;
            margin-left: pxToRem(-15);
            margin-top: pxToRem(-15);
            position: absolute;
            top: 50%;
            width: pxToRem(30);
            z-index: $limel-date-picker--flatpickr-adapter--flatpickr-day--after--z-index;
        }

        &.selected {
            &:after {
                background: $datepickerSelectedColor;
                color: $datepickerTextOnThemeColor;
            }
        }

        &.inRange,
        &.nextMonthDay:hover,
        &:hover {
            &:after {
                background: $datepickerHoverHighlightColor;
                color: $datepickerTextOnThemeColor;
                opacity: 0.5;
            }
        }
    }

    // The column with week numbers (including the title)
    .flatpickr-weekwrapper {
        width: pxToRem(40);
    }

    .flatpickr-time {
        // Ensure that the time picker is always shown
        // on a date time picker to get correct position
        // when popper decides to render (flip) it above
        // the input field due to an overflow.
        height: pxToRem(40);
    }
}

