import { MDCRipple } from '@limetech/mdc-ripple';
import { Component, Element, h, Method, Prop } from '@stencil/core';
import { IconSize } from '@limetech/lime-elements';

/**
 * @exampleComponent limel-example-icon-button-basic
 * @exampleComponent limel-example-icon-button-elevated
 * @exampleComponent limel-example-icon-button-toggle-state
 * @exampleComponent limel-example-icon-button-composite
 */
@Component({
    tag: 'limel-icon-button',
    shadow: true,
    styleUrl: 'icon-button.scss',
})
export class IconButton {
    public constructor() {
        this.removeFocusedStyleOnClick = this.removeFocusedStyleOnClick.bind(
            this
        );
    }

    /**
     * The icon to display.
     */
    @Prop({ reflect: true })
    public icon: string;

    /**
     * Set to `true` to give the button our standard "elevated" look, lifting
     * it off the flat layout.
     */
    @Prop({ reflect: true })
    public elevated = false;

    /**
     * The text to show to screenreaders and other assistive tech.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Set to `true` to disable the button.
     */
    @Prop({ reflect: true })
    public disabled = false;

    @Element()
    private host: HTMLLimelIconButtonElement;

    private mdcIconButtonRipple;

    /**
     * If the button is hidden or inside another element that is animating
     * while the button is instantiated, the hover-highlight may become
     * misaligned. If so, calling this method will make the button re-layout
     * the highlight.
     */
    @Method()
    public async relayout() {
        if (this.mdcIconButtonRipple) {
            this.mdcIconButtonRipple.layout();
        }
    }

    private removeFocusedStyleOnClick() {
        const mdcButton = this.host.shadowRoot.querySelector(
            '.mdc-icon-button'
        );
        mdcButton.classList.remove('mdc-ripple-upgraded--background-focused');
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.host.shadowRoot.querySelector('.mdc-icon-button');
        if (!element) {
            return;
        }

        this.mdcIconButtonRipple = new MDCRipple(element);
        this.mdcIconButtonRipple.unbounded = true;
        this.host.addEventListener('click', this.removeFocusedStyleOnClick);
    }

    public disconnectedCallback() {
        this.mdcIconButtonRipple?.destroy();
        this.host.removeEventListener('click', this.removeFocusedStyleOnClick);
    }

    public render() {
        const buttonAttributes: { tabindex?: string } = {};
        if (this.host.hasAttribute('tabindex')) {
            buttonAttributes.tabindex = this.host.getAttribute('tabindex');
        }

        const iconAttributes: { badge?: boolean; size?: IconSize } = {};
        if (this.elevated) {
            iconAttributes.badge = true;
            iconAttributes.size = 'small';
        }

        return (
            <button
                class="mdc-icon-button"
                disabled={this.disabled}
                aria-label={this.label}
                title={this.label}
                {...buttonAttributes}
            >
                <limel-icon name={this.icon} {...iconAttributes} />
            </button>
        );
    }
}

@import '../../style/mixins';
@import '../../style/internal/mdc-variables';
@import '@limetech/mdc-icon-button/mdc-icon-button';

@media (pointer: coarse) {
    .mdc-icon-button:hover::before {
        opacity: 0;
    }
}

/**
 * @prop --icon-background-color: Background color when attribute `elevated` is set to `true`.
 */

:host {
    display: inline-block;
}

:host([hidden]) {
    display: none;
}

:host([disabled]) {
    pointer-events: none;
}

:host([elevated]) {
    .mdc-icon-button {
        padding: pxToRem(9);
    }

    limel-icon {
        @include is-elevated-clickable;
    }
}

.mdc-icon-button {
    &:focus-visible {
        // only when non-pointer input is being used,
        // e.g. tabbed into using keyboard
        box-shadow: var(--shadow-depth-8-focused);
        border-radius: 50%;
    }
}

import { MDCTextField } from '@limetech/mdc-textfield';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { debounce } from 'lodash-es';
import {
    ARROW_DOWN,
    ARROW_DOWN_KEY_CODE,
    ARROW_UP,
    ARROW_UP_KEY_CODE,
    ENTER,
    ENTER_KEY_CODE,
    SPACE,
    SPACE_KEY_CODE,
    TAB,
    TAB_KEY_CODE,
} from '../../util/keycodes';
import { InputType } from './input-field.types';
import { ListItem } from '@limetech/lime-elements';
import { getHref, getTarget } from './link-helper';
import { JSXBase } from '@stencil/core/internal';
import { createRandomString } from '../../util/random-string';

interface LinkProperties {
    href: string;
    target?: string;
}

/**
 * @exampleComponent limel-example-input-field-text
 * @exampleComponent limel-example-input-field-text-multiple
 * @exampleComponent limel-example-input-field-number
 * @exampleComponent limel-example-input-field-autocomplete
 * @exampleComponent limel-example-input-field-icon-leading
 * @exampleComponent limel-example-input-field-icon-trailing
 * @exampleComponent limel-example-input-field-icon-both
 * @exampleComponent limel-example-input-field-showlink
 * @exampleComponent limel-example-input-field-error-icon
 * @exampleComponent limel-example-input-field-textarea
 * @exampleComponent limel-example-input-field-search
 * @exampleComponent limel-example-input-field-pattern
 */
@Component({
    tag: 'limel-input-field',
    shadow: true,
    styleUrl: 'input-field.scss',
})
export class InputField {
    /**
     * Disables the input field when `true`. Works exactly the same as
     * `readonly`. If either property is `true`, the input field will be
     * disabled.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Disables the input field when `true`. Works exactly the same as
     * `disabled`. If either property is `true`, the input field will be
     * disabled.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to indicate that the current value of the input field is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid = false;

    /**
     * The input label.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Optional helper text to display below the input field when it has focus
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * Set to `true` to indicate that the field is required.
     */
    @Prop({ reflect: true })
    public required = false;

    /**
     * The value of the field.
     */
    @Prop({ reflect: true })
    public value: string;

    /**
     * Trailing icon to show to the far right in the field.
     */
    @Prop({ reflect: true })
    public trailingIcon: string;

    /**
     * Leading icon to show to the far left in the field.
     */
    @Prop({ reflect: true })
    public leadingIcon: string;

    /**
     * Regular expression that the current value of the input field must match.
     * No forward slashes should be specified around the pattern.
     * Only used if type is `text`, `tel`, `email`, `url`, `urlAsText`,
     * `password`, or `search`.
     */
    @Prop({ reflect: true })
    public pattern: string;

    /**
     * Type of input.
     *
     * **Note** regarding type `url`: `limel-input` uses the native validation
     * built into the browser for many types of input fields. The native
     * validation for `url` is very strict, and does not allow relative urls,
     * nor any other formats that are not a "fully qualified" url. To allow
     * such urls, use the type `urlAsText` instead. `urlAsText` works exactly
     * like `text` in all regards, except that it enables use of the `showLink`
     * property.
     */
    @Prop({ reflect: true })
    public type: InputType = 'text';

    /**
     * Set to `true` to format the current value of the input field only
     * if the field is of type number.
     * The number format is determined by the current language of the browser.
     */
    @Prop({ reflect: true })
    public formatNumber = true;

    /**
     * Incremental values that are valid if the field type is `number`.
     */
    @Prop({ reflect: true })
    public step: number | 'any' = 'any';

    /**
     * Maximum allowed value if input type is `number`.
     */
    @Prop({ reflect: true })
    public max: number;

    /**
     * Minimum allowed value if input type is `number`.
     */
    @Prop({ reflect: true })
    public min: number;

    /**
     * Maximum length of the value if type is `password`, `search`, `tel`,
     * `text`, `url`, or `urlAsText`.
     */
    @Prop({ reflect: true })
    public maxlength: number;

    /**
     * Minimum length of the value if type is `password`, `search`, `tel`,
     * `text`, `url`, or `urlAsText`.
     */
    @Prop({ reflect: true })
    public minlength: number;

    /**
     * list of suggestions `value` can autocomplete to.
     */
    @Prop()
    public completions: string[] = [];

    /**
     * For inputs of type `email`, `tel`, `url`, and `urlAsText`, set this to
     * `true` to show a trailing icon with a `mailto:`,`tel:`, or normal link,
     * respectively. The default icon can be overridden using the `trailingIcon`
     * property.
     */
    @Prop({ reflect: true })
    public showLink = false;

    /**
     * Emitted when the input value is changed.
     */
    @Event()
    private change: EventEmitter<string>;

    /**
     * Emitted when `trailingIcon` or `leadingIcon` is set
     * and the icon is interacted with.
     */
    @Event()
    private action: EventEmitter<void>;

    @Element()
    private limelInputField: HTMLLimelInputFieldElement;

    @State()
    private isFocused: boolean = false;

    @State()
    private isModified: boolean = false;

    @State()
    public showCompletions: boolean = false;

    private mdcTextField: MDCTextField;
    private completionsList: ListItem[] = [];
    private portalId: string;

    constructor() {
        this.handleChange = this.handleChange.bind(this);
        this.handleIconKeyPress = this.handleIconKeyPress.bind(this);
        this.handleIconClick = this.handleIconClick.bind(this);
        this.onFocus = this.onFocus.bind(this);
        this.onBlur = this.onBlur.bind(this);
        this.onKeyDown = this.onKeyDown.bind(this);
        this.handleCompletionChange = this.handleCompletionChange.bind(this);
        this.layout = this.layout.bind(this);
        this.changeEmitter = this.changeEmitter.bind(this);
        this.getContainerClassList = this.getContainerClassList.bind(this);
        this.handleCloseMenu = this.handleCloseMenu.bind(this);

        const debounceTimeout = 100;
        this.changeEmitter = debounce(this.changeEmitter, debounceTimeout, {
            leading: false,
            maxWait: 300,
            trailing: true,
        });

        this.portalId = createRandomString();
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.limelInputField.shadowRoot.querySelector(
            '.mdc-text-field'
        );
        if (!element) {
            return;
        }

        this.mdcTextField = new MDCTextField(element);

        this.completionsList = [...this.completions].map((item) => {
            return { text: item };
        });

        window.addEventListener('resize', this.layout, { passive: true });
    }

    public disconnectedCallback() {
        if (this.mdcTextField) {
            this.mdcTextField.destroy();
        }

        window.removeEventListener('resize', this.layout);
    }

    public componentDidUpdate() {
        if (this.invalid) {
            this.mdcTextField.valid = false;
        }
    }

    public render() {
        const properties = this.getAdditionalProps();
        properties.id = 'tf-input-element';
        properties.class = 'mdc-text-field__input';
        properties.onInput = this.handleChange;
        properties.onFocus = this.onFocus;
        properties.onBlur = this.onBlur;
        properties.required = this.required;
        properties.disabled = this.disabled || this.readonly;

        const labelClassList = {
            'mdc-floating-label': true,
            'mdc-floating-label--float-above': !!this.value || this.isFocused,
        };

        return [
            <div class={this.getContainerClassList()}>
                {this.renderFormattedNumber()}
                {this.renderInput(properties)}
                {this.renderTextarea(properties)}
                <div class="mdc-notched-outline">
                    <div class="mdc-notched-outline__leading"></div>
                    <div class="mdc-notched-outline__notch">
                        <label
                            class={labelClassList}
                            htmlFor="tf-input-element"
                        >
                            {this.label}
                        </label>
                    </div>
                    <div class="mdc-notched-outline__trailing"></div>
                </div>
                {this.renderIcons()}
            </div>,
            this.renderHelperLine(),
            this.renderAutocompleteList(),
        ];
    }

    @Watch('value')
    protected valueWatcher(newValue: string) {
        if (!this.mdcTextField) {
            return;
        }

        if (newValue !== this.mdcTextField.value) {
            this.mdcTextField.value = newValue || '';
        }
    }

    private getContainerClassList() {
        const classList = {
            'mdc-text-field': true,
            'mdc-text-field--invalid': this.isInvalid(),
            'mdc-text-field--disabled': this.disabled || this.readonly,
            'mdc-text-field--required': this.required,
            'mdc-text-field--with-trailing-icon': !!this.getTrailingIcon(),
        };

        if (this.type === 'textarea') {
            classList['mdc-text-field--textarea'] = true;
            classList['has-helper-line'] =
                !!this.helperText || !!this.maxlength;
        } else {
            classList['mdc-text-field--outlined'] = true;
            classList['mdc-text-field--with-leading-icon'] = !!this.leadingIcon;
        }

        return classList;
    }

    private renderInput(
        properties: JSXBase.InputHTMLAttributes<HTMLInputElement>
    ) {
        if (this.type === 'textarea') {
            return;
        }

        const type = this.type === 'urlAsText' ? 'text' : this.type;

        return (
            <input
                {...properties}
                type={type}
                pattern={this.pattern}
                onWheel={this.handleWheel}
                onKeyDown={this.onKeyDown}
                value={this.value}
            />
        );
    }

    private renderTextarea(
        properties: JSXBase.TextareaHTMLAttributes<HTMLTextAreaElement>
    ) {
        if (this.type !== 'textarea') {
            return;
        }

        return <textarea {...properties}>{this.value}</textarea>;
    }

    private layout() {
        this.mdcTextField?.layout();
    }

    private getAdditionalProps() {
        const props: any = {};

        if (this.type === 'number') {
            props.step = this.step;
        }

        if (this.type === 'number' && this.min) {
            props.min = this.min;
        }

        if (this.type === 'number' && this.max) {
            props.max = this.max;
        }

        if (this.minlength) {
            props.minlength = this.minlength;
        }

        if (this.maxlength) {
            props.maxlength = this.maxlength;
        }

        return props;
    }

    private onFocus() {
        this.isFocused = true;
        this.showCompletions = true;
    }

    private onBlur() {
        this.isFocused = false;
        this.isModified = true;
    }

    private renderHelperLine() {
        if (
            !this.maxlength &&
            (this.helperText === null || this.helperText === undefined)
        ) {
            return;
        }

        return (
            <div class="mdc-text-field-helper-line">
                {this.renderHelperText()}
                {this.renderCharacterCounter()}
            </div>
        );
    }

    private renderHelperText() {
        if (this.helperText === null || this.helperText === undefined) {
            return;
        }

        const classList = {
            'mdc-text-field-helper-text': true,
            'mdc-text-field-helper-text--validation-msg': this.isInvalid(),
        };

        return <p class={classList}>{this.helperText}</p>;
    }

    private renderCharacterCounter() {
        if (!this.maxlength || this.type === 'number') {
            return;
        }

        const text: string = this.value || '';
        const label = `${text.length} / ${this.maxlength}`;

        return <div class="mdc-text-field-character-counter">{label}</div>;
    }

    private isInvalid() {
        if (this.invalid) {
            return true;
        }

        if (!this.isModified) {
            return false;
        }

        const element = this.getInputElement();

        return !(element && element.checkValidity());
    }

    private getInputElement(): HTMLInputElement | HTMLTextAreaElement {
        let elementName = 'input';
        if (this.type === 'textarea') {
            elementName = 'textarea';
        }

        return this.limelInputField.shadowRoot.querySelector(elementName);
    }

    private renderIcons() {
        if (this.type === 'textarea') {
            return;
        }

        const html = [];

        if (this.leadingIcon) {
            html.push(
                <i class="mdc-text-field__icon">
                    <limel-icon name={this.leadingIcon} />
                </i>
            );
        }

        const trailingIcon = this.getTrailingIcon();

        if (!this.isInvalid() && this.hasLink()) {
            html.push(this.renderLinkIcon(this.getLink(), trailingIcon));
        } else if (trailingIcon) {
            html.push(this.renderTrailingIcon(trailingIcon));
        }

        return html;
    }

    private hasLink() {
        return (
            this.showLink &&
            ['email', 'tel', 'url', 'urlAsText'].includes(this.type)
        );
    }

    private getLink() {
        const props: LinkProperties = { href: '' };
        switch (this.type) {
            case 'email':
                props.href = `mailto:${this.value}`;
                break;
            case 'tel':
                props.href = `tel:${this.value}`;
                break;
            default:
                props.href = getHref(this.value);
                props.target = getTarget(this.value);
        }

        return props;
    }

    private renderLinkIcon(linkProps: LinkProperties, icon: string) {
        return (
            <a
                {...linkProps}
                class="mdc-text-field__icon trailing-icon"
                tabindex={this.disabled || this.readonly ? '-1' : '0'}
                role="button"
            >
                <limel-icon name={icon} />
            </a>
        );
    }

    private renderTrailingIcon(icon: string) {
        return (
            <i
                onKeyPress={this.handleIconKeyPress}
                onClick={this.handleIconClick}
                class="mdc-text-field__icon trailing-icon"
                tabindex={this.isInvalid() ? '-1' : '0'}
                role="button"
            >
                <limel-icon name={icon} />
            </i>
        );
    }

    private getTrailingIcon() {
        if (this.isInvalid()) {
            return 'high_importance';
        }

        if (this.trailingIcon) {
            return this.trailingIcon;
        }

        if (this.showLink && this.type === 'email') {
            return 'filled_message';
        }

        if (this.showLink && this.type === 'tel') {
            return 'phone';
        }

        if (
            this.showLink &&
            (this.type === 'url' || this.type === 'urlAsText')
        ) {
            return 'external_link';
        }
    }

    private renderFormattedNumber() {
        if (this.type !== 'number' || !this.value) {
            return;
        }

        let renderValue = this.value;
        if (this.formatNumber) {
            renderValue = new Intl.NumberFormat(navigator.language).format(
                Number(this.value)
            );
        }

        return <span class="formatted-input">{renderValue}</span>;
    }

    /**
     * Key handler for the input field
     * Will change focus to the first/last item in the dropdown list to enable selection with the keyboard
     *
     * @param {KeyboardEvent} event event
     *
     * @returns {void}
     */

    private onKeyDown(event: KeyboardEvent): void {
        this.showCompletions = true;
        const isForwardTab =
            (event.key === TAB || event.keyCode === TAB_KEY_CODE) &&
            !event.altKey &&
            !event.metaKey &&
            !event.shiftKey;
        const isUp =
            event.key === ARROW_UP || event.keyCode === ARROW_UP_KEY_CODE;
        const isDown =
            event.key === ARROW_DOWN || event.keyCode === ARROW_DOWN_KEY_CODE;

        if (event.keyCode === TAB_KEY_CODE && event.shiftKey) {
            this.showCompletions = false;
        }

        if (!isForwardTab && !isUp && !isDown) {
            return;
        }

        const list = document.querySelector(` #${this.portalId} limel-list`);

        if (!list) {
            return;
        }

        event.preventDefault();
        if (isForwardTab || isDown) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-list-item:first-child'
            );
            listElement.focus();

            return;
        }

        if (isUp) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-list-item:last-child'
            );
            listElement.focus();
        }
    }

    private handleCompletionChange(event: CustomEvent<ListItem>) {
        event.stopPropagation();
        if (!event.detail) {
            return;
        }

        this.showCompletions = false;

        /*
         This change event doesn't need to be debounced in itself, but we want
         to make absolutely sure that an earlier change event that *has* been
         debounced doesn't emit after this one. Therefore, we run this through
         the same debounced emitter function. /Ads
         */
        this.changeEmitter(event.detail.text);
    }

    private renderAutocompleteList() {
        if (this.type === 'textarea' || !this.completions.length) {
            return;
        }

        return this.renderDropdown();
    }

    private renderPortal(content = null) {
        const dropdownZIndex = getComputedStyle(
            this.limelInputField
        ).getPropertyValue('--dropdown-z-index');

        return (
            <limel-portal
                visible={this.showCompletions}
                containerId={this.portalId}
                inheritParentWidth={true}
                containerStyle={{ 'z-index': dropdownZIndex }}
            >
                <limel-menu-surface
                    open={this.showCompletions}
                    allowClicksElement={this.limelInputField}
                    style={{
                        '--menu-surface-width': '100%',
                        'max-height': 'inherit',
                        display: 'flex',
                    }}
                    onDismiss={this.handleCloseMenu}
                >
                    {content}
                </limel-menu-surface>
            </limel-portal>
        );
    }

    private renderDropdown() {
        const content = this.renderListResult();

        return this.renderPortal(content);
    }

    private renderListResult() {
        const filteredCompletions: ListItem[] = this.filterCompletions(
            this.value
        );
        if (!filteredCompletions || filteredCompletions.length === 0) {
            return null;
        }

        return (
            <limel-list
                onChange={this.handleCompletionChange}
                type="selectable"
                items={filteredCompletions}
            />
        );
    }

    private handleCloseMenu() {
        this.showCompletions = false;
    }

    private filterCompletions = (filter: string) => {
        if (!filter) {
            return this.completionsList;
        }

        return this.completionsList.filter(
            (completion) =>
                completion.text.toLowerCase().indexOf(filter.toLowerCase()) > -1
        );
    };

    private handleChange(event) {
        event.stopPropagation();
        let value = event.target.value;

        if (this.type === 'number') {
            if (!value && event.data) {
                event.stopPropagation();

                return;
            }

            if (value) {
                value = Number(value);
            }
        }

        this.changeEmitter(value);
    }

    private changeEmitter(value: string) {
        this.change.emit(value);
    }

    private handleIconClick() {
        if (!this.isInvalid()) {
            this.action.emit();
        }
    }

    private handleIconKeyPress(event: KeyboardEvent) {
        const isEnter = event.key === ENTER || event.keyCode === ENTER_KEY_CODE;
        const isSpace = event.key === SPACE || event.keyCode === SPACE_KEY_CODE;

        if ((isSpace || isEnter) && !this.isInvalid()) {
            this.action.emit();
        }
    }

    private handleWheel() {
        // This empty event handler is here to circumvent a bug.
        // In some browsers (Chrome for example), hovering the input with
        // the input focused, and scrolling, will both change the value
        // AND scroll the page. We would prefer to never change the value
        // on scroll, instead always scrolling the page, but since we
        // haven't found a way to do that, this is the next best thing, as
        // it prevents the page from being scrolled, but only in the
        // circumstances when the value is changed by the scrolling.
        // Please test THOROUGHLY if you remove this event handler 😄
    }
}

@import '../../style/internal/mdc-variables';
@import '../../style/internal/shared_input-select-picker';
@import '../../style/mixins';

@import '@limetech/mdc-textfield/mdc-text-field';
@import '@limetech/mdc-ripple/mdc-ripple';
@import '@limetech/mdc-list/mdc-list';
@import '@limetech/mdc-elevation/mdc-elevation';
@import '@limetech/mdc-menu-surface/mdc-menu-surface';

@import './partial-styles/trailing-icon.scss';
@import './partial-styles/outlined-style-overrides.scss';

/**
 * @prop --textarea-height: Height of the field when type is set to `textarea`
 */

:host {
    position: relative;
    display: block;
}

:host([hidden]) {
    display: none;
}

:host([type='textarea']) {
    height: var(--textarea-height, 100%);

    .mdc-text-field.mdc-text-field--textarea {
        height: var(--textarea-height, 100%);

        &.has-helper-line {
            --heightOfHelperText: 1.1875rem;
            height: calc(
                var(--textarea-height, 100%) - var(--heightOfHelperText)
            );
        }
    }
    textarea.mdc-text-field__input {
        resize: vertical;
    }
}

.mdc-text-field {
    &:not(.mdc-text-field--disabled) {
        .mdc-floating-label {
            color: $label-color;
        }
    }
    &.mdc-text-field--disabled {
        .mdc-floating-label {
            color: $label-color-disabled;
        }
    }
}

.mdc-text-field {
    width: 100%;
    @include mdc-states-hover-opacity(0);
    @include mdc-states-focus-opacity(0, true);

    .mdc-floating-label {
        right: pxToRem(
            8
        ); //This is a hack to force the label truncate when container is too little. Otherwise due to position: absolute it won't. Kia
    }
}

.formatted-input {
    @include looks-like-input-label;

    z-index: $limel-input-field--formatted-input--z-index;
    padding: 0 pxToRem(40) 0 pxToRem(16);
    align-self: center;
    position: absolute;

    display: none;
}

:not(.mdc-text-field--focused):not(.mdc-text-field--invalid) {
    .formatted-input {
        display: block;
    }

    .mdc-text-field__input[type='number'] {
        color: transparent;
        -webkit-text-fill-color: transparent;
    }
}

.mdc-text-field--disabled {
    background-color: transparent;
    .formatted-input {
        @include looks-disabled;
    }
}

.autocomplete-list-container {
    position: relative;
}

.autocomplete-list {
    background-color: white;
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    max-height: pxToRem(200);
    overflow-y: auto;
}

.mdc-list-item {
    cursor: pointer;
