import {
    IconSize,
    ListItem,
    ListSeparator,
    ListType,
} from '@limetech/lime-elements';
import { MDCList, MDCListActionEvent } from '@limetech/mdc-list';
import { MDCMenu, MDCMenuItemEvent } from '@limetech/mdc-menu';
import { strings as listStrings } from '@limetech/mdc-list/constants';
import { strings as menuStrings } from '@limetech/mdc-menu/constants';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    Watch,
} from '@stencil/core';
import { ListRenderer } from './list-renderer';
import { ListRendererConfig } from './list-renderer-config';

const { ACTION_EVENT } = listStrings;
const { SELECTED_EVENT } = menuStrings;

/**
 * #### Custom styles
 *
 * | Class name              | Description                                                                                     |
 * | ----------------------- | ----------------------------------------------------------------------------------------------- |
 * | `has-striped-rows`      | Adds distinct styling by which every other row (list item) gets a darker background.            |
 * | `has-interactive-items` | Adds more affordance by applying an elevated visual effect on list item, when they are hovered. |
 *
 * @exampleComponent limel-example-list
 * @exampleComponent limel-example-list-secondary
 * @exampleComponent limel-example-list-selectable
 * @exampleComponent limel-example-list-icons
 * @exampleComponent limel-example-list-badge-icons
 * @exampleComponent limel-example-list-checkbox
 * @exampleComponent limel-example-list-checkbox-icons
 * @exampleComponent limel-example-list-radio-button
 * @exampleComponent limel-example-list-radio-button-icons
 * @exampleComponent limel-example-list-action
 * @exampleComponent limel-example-list-striped
 */
@Component({
    tag: 'limel-list',
    shadow: true,
    styleUrl: 'list.scss',
})
export class List {
    /**
     * List of items to display
     */
    @Prop()
    public items: Array<ListItem | ListSeparator>;

    /**
     * Set to `true` if the list should display larger icons with a background
     */
    @Prop()
    public badgeIcons: boolean;

    /**
     * Size of the icons in the list
     */
    @Prop()
    public iconSize: IconSize = 'small';

    /**
     * The type of the list, omit to get a regular list. Available types are:
     * `selectable`: regular list with single selection.
     * `radio`: radio button list with single selection.
     * `checkbox`: checkbox list with multiple selection.
     * `menu`: menu list with single selection.
     */
    @Prop()
    public type: ListType;

    @Element()
    private element: HTMLLimelListElement;

    private config: ListRendererConfig;
    private listRenderer = new ListRenderer();
    private mdcList: MDCList;
    private mdcMenu: MDCMenu;
    private multiple: boolean;
    private selectable: boolean;

    /**
     * Fired when a new value has been selected from the list. Only fired if selectable is set to true
     */
    @Event()
    private change: EventEmitter<ListItem | ListItem[]>;

    /**
     * Fired when an action has been selected from the action menu of a list item
     */
    @Event()
    protected select: EventEmitter<ListItem | ListItem[]>;

    constructor() {
        this.handleAction = this.handleAction.bind(this);
        this.handleMenuSelect = this.handleMenuSelect.bind(this);
    }

    public connectedCallback() {
        this.setup();
    }

    public disconnectedCallback() {
        this.teardown();
    }

    public componentDidLoad() {
        this.setup();
    }

    public render() {
        this.config = {
            badgeIcons: this.badgeIcons,
            type: this.type,
            iconSize: this.iconSize,
        };
        const html = this.listRenderer.render(this.items, this.config);

        if (this.type !== 'menu') {
            return html;
        }

        return <div class="mdc-menu mdc-menu-surface">{html}</div>;
    }

    @Watch('type')
    protected handleType() {
        this.setupListeners();
    }

    private setup() {
        if (this.type === 'menu') {
            this.setupMenu();
        } else {
            this.setupList();
        }

        this.setupListeners();
    }

    private setupList() {
        const element = this.element.shadowRoot.querySelector('.mdc-list');
        if (!element) {
            return;
        }

        this.mdcList = new MDCList(element);
    }

    private setupMenu() {
        const element = this.element.shadowRoot.querySelector('.mdc-menu');
        if (!element) {
            return;
        }

        this.mdcMenu = new MDCMenu(element);
    }

    private setupListeners() {
        if (this.type === 'menu') {
            this.setupMenuListeners();
        } else {
            this.setupListListeners();
        }
    }

    private setupListListeners() {
        if (!this.mdcList) {
            return;
        }

        this.mdcList.unlisten(ACTION_EVENT, this.handleAction);

        this.selectable = ['selectable', 'radio', 'checkbox', 'menu'].includes(
            this.type
        );
        this.multiple = this.type === 'checkbox';

        if (!this.selectable) {
            return;
        }

        this.mdcList.listen(ACTION_EVENT, this.handleAction);
        this.mdcList.singleSelection = !this.multiple;
    }

    private setupMenuListeners() {
        if (!this.mdcMenu) {
            return;
        }

        this.mdcMenu.unlisten(SELECTED_EVENT, this.handleMenuSelect);
        this.selectable = true;
        this.mdcMenu.listen(SELECTED_EVENT, this.handleMenuSelect);
    }

    private teardown() {
        this.mdcList?.unlisten(ACTION_EVENT, this.handleAction);
        this.mdcList?.destroy();

        this.mdcMenu?.unlisten(SELECTED_EVENT, this.handleMenuSelect);
        this.mdcMenu?.destroy();
    }

    private handleAction(event: MDCListActionEvent) {
        if (!this.multiple) {
            this.handleSingleSelect(event.detail.index);

            return;
        }

        this.handleMultiSelect(event.detail.index);
    }

    private handleMenuSelect(event: MDCMenuItemEvent) {
        this.handleSingleSelect(event.detail.index);
    }

    private handleSingleSelect(index: number) {
        const listItems = this.items.filter(this.isListItem) as ListItem[];
        if (listItems[index].disabled) {
            return;
        }

        const selectedItem: ListItem = listItems.find((item: ListItem) => {
            return !!item.selected;
        });

        if (selectedItem) {
            this.change.emit({ ...selectedItem, selected: false });
        }

        if (listItems[index] !== selectedItem) {
            if (this.type === 'menu') {
                this.change.emit({ ...listItems[index], selected: false });

                return;
            }

            this.change.emit({ ...listItems[index], selected: true });
        }
    }

    private handleMultiSelect(index: number) {
        const listItems = this.items.filter(this.isListItem) as ListItem[];
        if (listItems[index].disabled) {
            return;
        }

        const selectedItems: ListItem[] = listItems
            .filter((item: ListItem, listIndex: number) => {
                if (listIndex === index) {
                    // This is the item that was selected or deselected,
                    // so we negate its previous selection status.
                    return !item.selected;
                }

                // This is an item that didn't change, so we keep its selection status.
                return item.selected;
            })
            .map((item: ListItem) => {
                return { ...item, selected: true };
            });

        this.change.emit(selectedItems);
    }

    private isListItem(item: ListItem): boolean {
        return !('separator' in item);
    }
}

@import '../../style/internal/mdc-variables';

@import '@limetech/mdc-list/mdc-list';
@import '@limetech/mdc-menu/mdc-menu';

@import '../checkbox/checkbox.scss';

@import './radio-button/radio-button.scss';

@import '../../style/mixins';

$list-border-radius: 0.375rem; // 6px

/**
 * @prop --icon-background-color: Color to use for icon background when `badgeIcons=true`.
 * @prop --icon-color: Color to use for icon. Defaults to grey when `badgeIcons=false`. Defaults to white when `badgeIcons=true`.
 */

:host {
    display: block;
}

:host([hidden]) {
    display: none;
}

:host(.has-striped-rows) {
    .mdc-list {
        border: 1px solid #f6f6f7;
    }

    .mdc-list-item {
        &:nth-child(even) {
            background-color: rgba(#fafafb, 1);
        }
        &:nth-child(odd) {
            background-color: rgba(#fafafb, 0.5);
        }
    }
}

:host(.has-interactive-items) {
    .mdc-list-item {
        &:not(.mdc-list-item--disabled) {
            @include is-flat-clickable();
            &:hover {
                z-index: $limel-list--has-interactive-items--mdc-list-item--hover--z-index;
                background-color: #fff;

                &:before {
                    background-color: #fff;
                }
            }
        }
    }
}

.mdc-menu {
    max-height: 70vh; // force tall menus render inside the viewport when menu is at the bottom of the screen
}

.mdc-list {
    --mdc-theme-text-icon-on-background: var(--icon-color, rgb(157, 157, 157));
    padding: 0;
    border-radius: $list-border-radius;

    .mdc-list-item {
        &.mdc-list-item--disabled {
            cursor: not-allowed;

            limel-icon {
                opacity: 0.38; // similar to `mdc-list-item__text` when disabled
            }
        }

        &:first-child {
            border-top-left-radius: $list-border-radius;
            border-top-right-radius: $list-border-radius;
        }
        &:last-child {
            border-bottom-right-radius: $list-border-radius;
            border-bottom-left-radius: $list-border-radius;
        }
    }

    &.selectable .mdc-list-item:not(.mdc-list-item--disabled) {
        cursor: pointer;
    }

    &.mdc-list--avatar-list {
        position: relative;

        limel-icon {
            background-color: var(
                --icon-background-color,
                var(--lime-light-grey)
            );
            color: var(--icon-color, white);
        }

        hr.mdc-list-divider {
            position: absolute;
            bottom: 0;

            &.mdc-list-divider--inset {
                --icon-width: #{pxToRem(41)};
                --icon-right-padding: #{pxToRem(16)};
                --list-right-padding: #{pxToRem(16)};
                margin-left: calc(
                    var(--icon-width) + var(--icon-right-padding)
                );
                width: calc(
                    100% - var(--icon-width) - var(--icon-right-padding) -
                        var(--list-right-padding)
                );

                &.x-small {
                    --icon-width: #{pxToRem(23)};
                }

                &.small {
                    --icon-width: #{pxToRem(30)};
                }

                &.medium {
                    --icon-width: #{pxToRem(40)};
                }

                &.large {
                    --icon-width: #{pxToRem(46)};
                }
            }
        }

        .mdc-list-item:last-child hr.mdc-list-divider {
            display: none;
        }
    }

    .mdc-list-item[role='menuitem'] {
        font-size: pxToRem(13);

        .mdc-list-item__graphic {
            margin-right: pxToRem(14);
        }
    }

    &.mdc-list--two-line {
        .mdc-list-item__primary-text {
            margin-top: pxToRem(-12);
        }
        .mdc-list-item__text {
            align-self: center;
        }
        &.list--compact {
            .mdc-list-item {
                height: 4rem;

                .mdc-list-item__text {
                    margin-top: pxToRem(-4);
                }
            }
        }
    }

    // Tweaks to display the icon correctly in Edge
    limel-icon.mdc-list-item__graphic {
        display: block;
    }
}

.mdc-list:not(.mdc-list--avatar-list) {
    limel-icon.mdc-list-item__graphic {
        // Tweaks to make icon lists align nicely with badge-icon lists.
        &[size='x-small'] {
            margin-right: pxToRem(4);
        }
        &[size='small'] {
            margin-right: pxToRem(8);
        }
        &[size='medium'] {
            margin-right: pxToRem(8);
        }
        &[size='large'] {
            margin-right: pxToRem(12);
        }
    }
}

import { ListItem, ListSeparator } from '@limetech/lime-elements';
import {
    Component,
    Event,
    EventEmitter,
    h,
    Prop,
    Element,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { OpenDirection } from './menu.types';

/**
 * @slot trigger - Element to use as a trigger for the menu
 * @exampleComponent limel-example-menu
 */
@Component({
    tag: 'limel-menu',
    shadow: true,
    styleUrl: 'menu.scss',
})
export class Menu {
    /**
     * Is displayed on the default trigger button.
     */
    @Prop({ reflect: true })
    public label = '';

    /**
     * A list of items and separators to show in the menu.
     */
    @Prop()
    public items: Array<ListItem | ListSeparator> = [];

    /**
     * Sets the disabled state of the menu.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Decides if the menu should open right or left.
     */
    @Prop({ reflect: true })
    public openDirection: OpenDirection = 'right';

    /**
     * Sets the open state of the menu.
     */
    @Prop({ mutable: true, reflect: true }) // eslint-disable-line @stencil/strict-mutable
    public open = false;

    /**
     * Defines whether the menu should show badges.
     */
    @Prop({ reflect: true })
    public badgeIcons = false;

    /**
     * Defines whether the menu should have a fixed position on the screen.
     */
    @Prop()
    public fixed = false;

    /**
     * Is emitted when the menu is cancelled.
     */
    @Event()
    private cancel: EventEmitter<void>;

    /**
     * Is emitted when a menu item is selected.
     */
    @Event()
    private select: EventEmitter<ListItem | ListItem[]>;

    @Element()
    private host: HTMLLimelMenuElement;

    private portalId: string;

    constructor() {
        this.portalId = createRandomString();
    }

    public render() {
        const dropdownZIndex = getComputedStyle(this.host).getPropertyValue(
            '--dropdown-z-index'
        );
        const portalClasses = {
            'limel-portal--fixed': this.fixed,
        };
        const portalPosition = this.getPortalPosition();

        return (
            <div class="mdc-menu-surface--anchor" onClick={this.onTriggerClick}>
                <slot name="trigger">{this.renderTrigger()}</slot>
                <limel-portal
                    class={portalClasses}
                    style={portalPosition}
                    visible={this.open}
                    containerId={this.portalId}
                    openDirection={this.openDirection}
                    position={this.fixed ? 'fixed' : 'absolute'}
                    containerStyle={{ 'z-index': dropdownZIndex }}
                >
                    <limel-menu-surface
                        open={this.open}
                        onDismiss={this.onClose}
                    >
                        <limel-list
                            items={this.items}
                            type="menu"
                            badgeIcons={this.badgeIcons}
                            onChange={this.onListChange}
                        />
                    </limel-menu-surface>
                </limel-portal>
            </div>
        );
    }

    private renderTrigger() {
        return (
            <button
                class={`
                    menu__trigger
                    ${this.disabled ? '' : 'menu__trigger-enabled'}
                `}
                disabled={this.disabled}
            >
                <span>{this.label}</span>
            </button>
        );
    }

    private onClose = () => {
        this.cancel.emit();
        this.open = false;
    };

    private onTriggerClick = (event: MouseEvent) => {
        event.stopPropagation();
        if (this.disabled) {
            return;
        }

        this.open = !this.open;
    };

    private onListChange = (event) => {
        this.items = this.items.map((item: ListItem) => {
            if (item === event.detail) {
                return event.detail;
            }

            return item;
        });
        this.select.emit(event.detail);
        this.open = false;
    };

    private getPortalPosition() {
        if (!this.fixed) {
            return {};
        }

        const rect = this.host.getBoundingClientRect();
        const portalPosition = {
            top: `${rect.y + rect.height}px`,
            left: `${rect.x}px`,
        };

        if (this.openDirection === 'left') {
            portalPosition.left = `${rect.x + rect.width}px`;
        }

        return portalPosition;
    }
}

