import { Corner, MDCMenuSurface } from '@limetech/mdc-menu-surface';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
} from '@stencil/core';
import { isDescendant } from '../../util/dom';
import {
    ESCAPE,
    ESCAPE_KEY_CODE,
    TAB,
    TAB_KEY_CODE,
} from '../../util/keycodes';

/**
 * @slot - Content to put inside the surface
 * @private
 */
@Component({
    tag: 'limel-menu-surface',
    shadow: true,
    styleUrl: 'menu-surface.scss',
})
export class MenuSurface {
    /**
     * True if the menu surface is open, false otherwise
     */
    @Prop()
    public open = false;

    /**
     * Clicks in this element should not be prevented when the menu surface is open
     */
    @Prop()
    public allowClicksElement: HTMLElement;

    /**
     * Emitted when the menu surface is dismissed and should be closed
     */
    @Event()
    public dismiss: EventEmitter<void>;

    @Element()
    private host: HTMLLimelMenuSurfaceElement;

    private menuSurface: MDCMenuSurface;

    constructor() {
        this.handleDocumentClick = this.handleDocumentClick.bind(this);
        this.handleKeyDown = this.handleKeyDown.bind(this);
        this.handleResize = this.handleResize.bind(this);
    }

    public connectedCallback() {
        this.setup();
    }

    public disconnectedCallback() {
        this.teardown();
    }

    public componentDidLoad() {
        this.setup();
    }

    public render() {
        const classList = {
            'mdc-menu': true,
            'mdc-menu-surface': true,
            'mdc-menu-surface--open': this.open,
            'mdc-elevation-transition': true,
            'mdc-elevation--z4': true,
        };

        return (
            <div class={classList} tabindex="-1">
                <slot />
            </div>
        );
    }

    private setup() {
        const menuElement: HTMLElement = this.host.shadowRoot.querySelector(
            '.mdc-menu-surface'
        );
        if (!menuElement) {
            return;
        }

        this.menuSurface = new MDCMenuSurface(menuElement);
        this.menuSurface.setAnchorCorner(Corner.TOP_START);

        document.addEventListener('mousedown', this.handleDocumentClick, {
            capture: true,
        });
        this.host.addEventListener('keydown', this.handleKeyDown);
        window.addEventListener('resize', this.handleResize, {
            passive: true,
        });
    }

    private teardown() {
        this.menuSurface?.destroy();
        document.removeEventListener('mousedown', this.handleDocumentClick, {
            capture: true,
        });
        this.host.removeEventListener('keydown', this.handleKeyDown);
        window.removeEventListener('resize', this.handleResize);
    }

    private handleDocumentClick(event) {
        const elementPath = event.path || [];

        if (!this.open) {
            return;
        }

        if (isDescendant(event.target, this.host)) {
            return;
        }

        if (this.allowClicksElement) {
            const clickedInAllowedElement = elementPath.includes(
                this.allowClicksElement
            );

            if (clickedInAllowedElement) {
                return;
            }
        }

        this.dismiss.emit();
        this.preventClickEventPropagation();
    }

    private handleResize() {
        if (this.open) {
            this.dismiss.emit();
        }
    }

    private preventClickEventPropagation() {
        // When the menu surface is open, we want to stop the `click` event from propagating
        // when clicking outside the surface itself. This is to prevent any dialog that might
        // be open from closing, etc. However, when dragging a scrollbar no `click` event is emitted,
        // only mousedown and mouseup. So we listen for `mousedown` and attach a one-time listener
        // for `click`, so we can capture and "kill" it.
        document.addEventListener('click', this.stopEvent, {
            capture: true,
            once: true,
        });
        // We also capture and "kill" the next `mouseup` event.
        document.addEventListener('mouseup', this.stopEvent, {
            capture: true,
            once: true,
        });
        // If the user dragged the scrollbar, no `click` event happens. So when we get the
        // `mouseup` event, remove the handler for `click` if it's still there.
        // Otherwise, we would catch the next click even though the menu is no longer open.
        document.addEventListener(
            'mouseup',
            () => {
                document.removeEventListener('click', this.stopEvent, {
                    capture: true,
                });
            },
            {
                once: true,
            }
        );
    }

    private stopEvent(event) {
        event.stopPropagation();
        event.preventDefault();
    }

    private handleKeyDown(event: KeyboardEvent) {
        const isEscape =
            event.key === ESCAPE || event.keyCode === ESCAPE_KEY_CODE;
        const isTab = event.key === TAB || event.keyCode === TAB_KEY_CODE;

        if (this.open && (isEscape || isTab)) {
            event.stopPropagation();
            this.dismiss.emit();
        }
    }
}

@import '../../style/internal/mdc-variables';
@import '@limetech/mdc-menu-surface/mdc-menu-surface';
@import '@limetech/mdc-elevation/mdc-elevation';
@import '@limetech/mdc-menu/mdc-menu';

/**
 * @prop --menu-surface-width: Width of the menu surface. Defaults to `auto`
 */

:host {
    display: block;
}

.mdc-menu-surface {
    width: var(--menu-surface-width, auto);
    max-height: 100%;
    position: relative;
}

import { Chip, ListItem, Searcher } from '@limetech/lime-elements';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import AwesomeDebouncePromise from 'awesome-debounce-promise';
import { isDescendant } from '../../util/dom';
import {
    ARROW_DOWN,
    ARROW_DOWN_KEY_CODE,
    ARROW_UP,
    ARROW_UP_KEY_CODE,
    ESCAPE,
    ESCAPE_KEY_CODE,
    TAB,
    TAB_KEY_CODE,
} from '../../util/keycodes';
import { createRandomString } from '../../util/random-string';

const SEARCH_DEBOUNCE = 500;
const CHIP_SET_TAG_NAME = 'limel-chip-set';

/**
 * @exampleComponent limel-example-picker
 * @exampleComponent limel-example-picker-multiple
 * @exampleComponent limel-example-picker-icons
 * @exampleComponent limel-example-picker-empty-suggestions
 * @exampleComponent limel-example-picker-leading-icon
 */
@Component({
    tag: 'limel-picker',
    shadow: true,
    styleUrl: 'picker.scss',
})
export class Picker {
    /**
     * True if the picker should be disabled
     */
    @Prop()
    public disabled: boolean = false;

    /**
     * Set to `true` to disable adding and removing items,
     * but allow interaction with existing items.
     */
    @Prop({ reflect: true })
    public readonly: boolean = false;

    /**
     * Text to display for the input field of the picker
     */
    @Prop()
    public label: string;

    /**
     * Search label to display in the input field when searching
     */
    @Prop()
    public searchLabel: string;

    /**
     * Leading icon to show to the far left in the text field
     */
    @Prop()
    public leadingIcon: string;

    /**
     * A message to display when the search returned an empty result
     */
    @Prop()
    public emptyResultMessage: string;

    /**
     * True if the control requires a value
     */
    @Prop()
    public required: boolean = false;

    /**
     * Currently selected value or values
     */
    @Prop()
    public value: ListItem<number | string> | Array<ListItem<number | string>>;

    /**
     * A search function that takes a search-string as an argument,
     * and returns a promise that will eventually be resolved with
     * an array of `ListItem`:s.
     */
    @Prop()
    public searcher: Searcher;

    /**
     * True if multiple values are allowed
     */
    @Prop()
    public multiple: boolean = false;

    /**
     * True if the dropdown list should be displayed without cutting the content
     *
     * @deprecated This was used for a workaround, and isn't needed any
     * longer. Setting it has no effect, and the property will be removed
     * in the next major version.
     */
    @Prop()
    public displayFullList: boolean = false;

    /**
     * Fired when a new value has been selected from the picker
     */
    @Event()
    private change: EventEmitter<
        ListItem<number | string> | Array<ListItem<number | string>>
    >;

    /**
     * Fired when clicking on a selected value
     */
    @Event()
    private interact: EventEmitter<ListItem<number | string>>;

    @State()
    private items: Array<ListItem<number | string>>;

    @State()
    private textValue: string = '';

    @State()
    private loading: boolean = false;

    @State()
    private chips: Chip[] = [];

    @Element()
    private host: HTMLLimelPickerElement;

    // Should NOT be decorated with State(), since this
    // should not trigger a re-render by itself.
    private chipSetEditMode = false;

    private debouncedSearch: Searcher;
    private chipSet: HTMLLimelChipSetElement;
    private portalId: string;

    constructor() {
        this.handleTextInput = this.handleTextInput.bind(this);
        this.handleInputKeyDown = this.handleInputKeyDown.bind(this);
        this.handleDropdownKeyDown = this.handleDropdownKeyDown.bind(this);
        this.handleInputFieldFocus = this.handleInputFieldFocus.bind(this);
        this.handleChange = this.handleChange.bind(this);
        this.handleInteract = this.handleInteract.bind(this);
        this.handleListChange = this.handleListChange.bind(this);
        this.handleStopEditAndBlur = this.handleStopEditAndBlur.bind(this);
        this.createDebouncedSearcher = this.createDebouncedSearcher.bind(this);
        this.handleCloseMenu = this.handleCloseMenu.bind(this);

        this.portalId = createRandomString();
    }

    public componentWillLoad() {
        this.chips = this.createChips(this.value);
    }

    public componentDidLoad() {
        this.createDebouncedSearcher(this.searcher);
        this.chipSet = this.host.shadowRoot.querySelector(CHIP_SET_TAG_NAME);
    }

    public async componentWillUpdate() {
        this.chipSetEditMode = false;
        if (this.chipSet) {
            this.chipSetEditMode = await this.chipSet.getEditMode();
        }
    }

    public render() {
        const iconColors = this.chips.some((chip: Chip) => {
            return 'iconColor' in chip && !!chip.iconColor;
        });
        const style = {};

        if (iconColors) {
            style['--icon-color'] = 'white';
        }

        const props: {
            maxItems?: number;
        } = {};

        if (!this.multiple) {
            props.maxItems = 1;
        }

        return [
            <limel-chip-set
                style={style}
                type="input"
                label={this.label}
                leadingIcon={this.leadingIcon}
                value={this.chips}
                disabled={this.disabled}
                readonly={this.readonly}
                required={this.required}
                searchLabel={this.searchLabel}
                onInput={this.handleTextInput}
                onKeyDown={this.handleInputKeyDown}
                onChange={this.handleChange}
                onInteract={this.handleInteract}
                onStartEdit={this.handleInputFieldFocus}
                onStopEdit={this.handleStopEditAndBlur}
                emptyInputOnBlur={false}
                {...props}
            />,
            this.renderDropdown(),
        ];
    }

    @Watch('value')
    protected onChangeValue() {
        this.chips = this.createChips(this.value);
    }

    @Watch('searcher')
    protected createDebouncedSearcher(newValue: Searcher) {
        if (typeof newValue !== 'function') {
            return;
        }

        this.debouncedSearch = AwesomeDebouncePromise(
            newValue,
            SEARCH_DEBOUNCE
        );
    }

    private createChips(value: ListItem | ListItem[]): Chip[] {
        if (!value) {
            return [];
        }

        if (this.multiple) {
            const listItems: ListItem[] = value as ListItem[];

            return listItems.map(this.createChip);
        }

        const listItem: ListItem = value as ListItem;

        return [this.createChip(listItem)];
    }

    private createChip(listItem: ListItem): Chip {
        return {
            id: `${listItem.value}`,
            text: listItem.text,
            removable: true,
            icon: listItem.icon,
            iconColor: listItem.iconColor,
            value: listItem,
        };
    }

    /**
     * Renders the dropdown with the items to pick from, or a spinner if the picker
     * is waiting for items to be received
     *
     * @returns {HTMLElement} picker dropdown
     */
    private renderDropdown() {
        const content = this.getDropdownContent();

        return this.renderPortal(content);
    }

    private getDropdownContent() {
        if (this.isFull()) {
            return;
        }

        if (!this.chipSetEditMode) {
            return;
        }

        if (this.loading) {
            return this.renderSpinner();
        }

        if (!this.items || !this.items.length) {
            return this.renderEmptyMessage();
        }

        return this.renderListResult();
    }

    /**
     * Returns true if the picker is "full"
     * The picker is considered to be full if it has a value and only one is allowed
     *
     * @returns {boolean} true if the picker is full
     */
    private isFull(): boolean {
        return !this.multiple && !!this.value;
    }

    private renderSpinner() {
        return (
            <div style={{ 'padding-top': '6px', 'text-align': 'center' }}>
                <limel-spinner class={'dropdown--spinner'} />
            </div>
        );
    }

    private renderEmptyMessage() {
        if (!this.emptyResultMessage) {
            return;
        }

        const style = {
            color: 'var(--lime-light-grey, #{$lime-light-grey})',
            'text-align': 'center',
        };

        return <p style={style}>{this.emptyResultMessage}</p>;
    }

    private renderListResult() {
        const hasIcons = this.items.some((item) => {
            return 'icon' in item && !!item.icon;
        });

        return (
            <limel-list
                badgeIcons={hasIcons}
                onChange={this.handleListChange}
                type="selectable"
                items={this.items}
            />
        );
    }

    private renderPortal(content = null) {
        const dropdownZIndex = getComputedStyle(this.host).getPropertyValue(
            '--dropdown-z-index'
        );

        return (
            <limel-portal
                visible={!!content}
                containerId={this.portalId}
                inheritParentWidth={true}
                containerStyle={{ 'z-index': dropdownZIndex }}
            >
                <limel-menu-surface
                    open={!!content}
                    allowClicksElement={this.host}
                    style={{
                        '--menu-surface-width': '100%',
                        'max-height': 'inherit',
                        display: 'flex',
                    }}
                    onDismiss={this.handleCloseMenu}
                >
                    {content}
                </limel-menu-surface>
            </limel-portal>
        );
    }

    /**
     * Check if a descendant still has focus. If not, reset text value and search result.
     *
     * @returns {void}
     */
    private handleStopEditAndBlur() {
        // In browsers where shadow DOM is not supported activeElement on shadowRoot will return null
        // However, document.activeElement will return the actual focused element instead of the outermost shadow host
        const element =
            this.host.shadowRoot.activeElement || document.activeElement;
        const portalElement = document.querySelector(`#${this.portalId}`);
        if (
            isDescendant(element as HTMLElement, this.host) ||
            isDescendant(element as HTMLElement, portalElement as HTMLElement)
        ) {
            return;
        }

        this.clearInputField();
    }

    /**
     * Input handler for the input field
     *
     * @param {InputEvent} event event
     *
     * @returns {void}
     */
    private async handleTextInput(event) {
        event.stopPropagation();

        const query = event.detail;
        this.textValue = query;
        this.loading = true;

        // If the search-query is an empty string, bypass debouncing.
        const searchFn = query === '' ? this.searcher : this.debouncedSearch;
        const result = await searchFn(query);
        this.handleSearchResult(query, result);
    }

    /**
     * Change handler for the list
     *
     * @param {CustomEvent} event event
     *
     * @returns {void}
     */
    private handleListChange(event: CustomEvent<ListItem>) {
        event.stopPropagation();
        if (!this.value || this.value !== event.detail) {
            let newValue: ListItem | ListItem[] = event.detail;
            if (this.multiple) {
                newValue = [...(this.value as ListItem[]), event.detail];
            }

            this.change.emit(newValue);
            this.items = [];
        }

        if (this.multiple) {
            this.chipSet?.setFocus(true);
        }
    }

    /**
     * Focus handler for the chip set
     * Prevent focus if the picker has a value and does not support multiple values
     *
     * @returns {void}
     */
    private async handleInputFieldFocus() {
        this.loading = true;
        const query = this.textValue;
        const result = await this.searcher(query);
        this.handleSearchResult(query, result);
    }

    private handleChange(event: CustomEvent<Chip | Chip[]>) {
        event.stopPropagation();

        let newValue = null;
        if (this.multiple) {
            const chips = event.detail as Chip[];
            newValue = chips.map((chip) => {
                return (this.value as ListItem[]).find((item) => {
                    return `${item.value}` === chip.id;
                });
            });
        }

        if (!this.multiple && !newValue) {
            this.chipSet?.setFocus(true);
        }

        this.change.emit(newValue);
    }

    private handleInteract(event: CustomEvent<Chip>) {
        event.stopPropagation();
        this.interact.emit(event.detail ? event.detail.value : event.detail);
    }

    /**
     * Key handler for the input field
     * Will change focus to the first/last item in the dropdown list to enable selection with the keyboard
     *
     * @param {KeyboardEvent} event event
     *
     * @returns {void}
     */
    private handleInputKeyDown(event: KeyboardEvent) {
        const isForwardTab =
            (event.key === TAB || event.keyCode === TAB_KEY_CODE) &&
            !event.altKey &&
            !event.metaKey &&
            !event.shiftKey;
        const isUp =
            event.key === ARROW_UP || event.keyCode === ARROW_UP_KEY_CODE;
        const isDown =
            event.key === ARROW_DOWN || event.keyCode === ARROW_DOWN_KEY_CODE;

        if (!isForwardTab && !isUp && !isDown) {
            return;
        }

        const list = document.querySelector(` #${this.portalId} limel-list`);
        if (!list) {
            return;
        }

        event.preventDefault();

        if (isForwardTab || isDown) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-list-item:first-child'
            );
            listElement.focus();

            return;
        }

        if (isUp) {
            const listElement: HTMLElement = list.shadowRoot.querySelector(
                '.mdc-list-item:last-child'
            );
            listElement.focus();
        }
    }

    /**
     * Key handler for the dropdown
     *
     * @param {KeyboardEvent} event event
     *
     * @returns {void}
     */
    private handleDropdownKeyDown(event: KeyboardEvent) {
        const isEscape =
            event.key === ESCAPE || event.keyCode === ESCAPE_KEY_CODE;

        if (isEscape) {
            event.preventDefault();
            this.textValue = '';
            this.chipSet.setFocus(true);
        }
    }

    private handleSearchResult(query: string, result: ListItem[]) {
        if (query === this.textValue) {
            this.items = result;
            if (this.multiple) {
                const values = this.value as ListItem[];
                this.items = result.filter((item) => {
                    return !values.includes(item);
                });
            }

            this.loading = false;
        }
    }

    private handleCloseMenu() {
        this.clearInputField();
    }

    private clearInputField() {
        this.chipSet.emptyInput();
        this.textValue = '';
        this.handleSearchResult('', []);
    }
}

@import '../../style/internal/mdc-variables';

// Note! The `--dropdown-z-index` property is used from `picker.tsx`.
/**
+ * @prop --dropdown-z-index: z-index of the dropdown menu.
+ */

:host {
    position: relative;
    display: block;
}

:host([hidden]) {
    display: none;
}

import {
    Component,
    h,
    Prop,
    Element,
    Event,
    EventEmitter,
    Watch,
} from '@stencil/core';
import { createRandomString } from '../../util/random-string';
import { zipObject } from 'lodash-es';
import { portalContains } from '../portal/contains';

/**
 * A popover is an impermanent layer that is displayed on top of other content
 * when user taps an element that triggers the popover. This element can be
 * practically anything, a button, piece of text, and icon, etc.
 *
 * Popovers are nonmodal, thus they don't have a semi-transparent backdrop layer
 * that darkens the content below them. Also for the same reason, they can be
 * dismissed by tapping or clicking another part of the screen, but not by a
 * clicking a button or link on the popover itself.
 *
 * :::warning
 * The component is emitting a close event when you click outside its container.
 * However, it’s up to you as consumer to react properly on this event and
 * hide the component (preferably by setting the property `open` to `false`).
 * :::
 *
 * ## Usage
 * Use a popover to show **options** or **information** related to the trigger
 * onscreen. A typical use case for popovers is a tooltip, in which you show
 * help text or contextual information to users.
 *
 * Popovers are most appropriate on larger screens but can be used on smaller
 * screens too, as long as their content is responsive and takes into account
 * the context they are displayed on.
 *
 * When a popover is displayed, interactions with other controls are blocked,
 * until user dismisses the popover.
 *
 *
 * ## Layout
 * Popovers has only one slot in which you can import a custom web-component.
 *
 * :::note
 * You must make sure that web-components that you import into the slot has
 * a `width: 100%;` style so that it can horizontally stretch across the popover.
 *
 * However, `width` of the popover can be controlled by specifying a CSS variable
 * of `--popover-surface-width`. If you don't specify any width, the popover
 * will grow as wide as its content.
 * :::
 *
 * :::important
 * Do not make a popover too big. They should never take over the entire screen.
 * If your content is that big, you should probably be using a Modal instead.
 * :::
 *
 *
 * @slot - Content to put inside the surface
 * @exampleComponent limel-example-popover
 * @private
 */
@Component({
    tag: 'limel-popover',
    shadow: true,
    styleUrl: 'popover.scss',
})
export class Popover {
    /**
     * True if the content within the popover should be visible
     */
    @Prop()
    public open = false;

    /**
     * Emits an event when the component is closing
     */
    @Event()
    private close: EventEmitter<void>;

    @Element()
    private host: HTMLLimelPopoverElement;

    private portalId: string;

    constructor() {
        this.portalId = createRandomString();
        this.globalClickListener = this.globalClickListener.bind(this);
    }

    @Watch('open')
    protected watchOpen() {
        this.setupClickHandler();
    }

    public componentWillLoad() {
        this.setupClickHandler();
    }

    private setupClickHandler() {
        if (this.open) {
            document.addEventListener('click', this.globalClickListener, {
                capture: true,
            });
        } else {
            document.removeEventListener('click', this.globalClickListener);
        }
    }

    public render() {
        const cssProperties = this.getCssProperties();
        const popoverZIndex = getComputedStyle(this.host).getPropertyValue(
            '--popover-z-index'
        );

        return (
            <div class="trigger-anchor">
                <slot name="trigger"></slot>
                <limel-portal
                    visible={this.open}
                    containerId={this.portalId}
                    containerStyle={{ 'z-index': popoverZIndex }}
                >
                    <limel-popover-surface
                        contentCollection={this.host.children}
                        style={cssProperties}
                    />
                </limel-portal>
            </div>
        );
    }

    private globalClickListener(event: MouseEvent) {
        const element: HTMLElement = event.target as HTMLElement;
        const clickedInside = portalContains(this.host, element);
        if (this.open && !clickedInside) {
            event.stopPropagation();
            this.close.emit();
        }
    }

    private getCssProperties() {
        const propertyNames = [
            '--popover-surface-width',
            '--popover-body-background-color',
            '--popover-border-radius',
        ];
        const style = getComputedStyle(this.host);
        const values = propertyNames.map((property) => {
            return style.getPropertyValue(property);
        });

        return zipObject(propertyNames, values);
    }
}

/**
 * @prop --popover-surface-width: Width of the popover surface. defaults to `auto`
 * @prop --popover-body-background-color: Background color of popover body, defaults to `--contrast-100`.
 * @prop --popover-border-radius: Border radius of popover, defaults to `0.75rem`.
 * @prop --popover-z-index: z-index of the popover.
 */

.trigger-anchor {
    display: inline-block;
    position: relative;
}

import { Component, h, Prop, Element } from '@stencil/core';

/**
 * @slot - Content to put inside the surface
 * @private
 */

@Component({
    tag: 'limel-popover-surface',
    shadow: true,
    styleUrl: 'popover-surface.scss',
})
export class PopoverSurface {
    /**
     * Content to render
     */
    @Prop()
    public contentCollection: HTMLCollection;

    @Element()
    private host: HTMLLimelPopoverSurfaceElement;

    public componentDidLoad() {
        this.appendElement();
    }

    public render() {
        return <div class="limel-popover-surface" tabindex="0" />;
    }

    private appendElement() {
        const portalContainer = this.host.shadowRoot.querySelector(
            '.limel-popover-surface'
        );

        Array.from(this.contentCollection).forEach((child) => {
            if (child.slot === 'trigger') {
                return;
            }

            portalContainer.appendChild(child);
        });
    }
}

@import '../../style/functions';
@import '../../style/internal/variables';

.limel-popover-surface {
    position: relative;
    display: flex;
    flex-direction: column;

    width: var(--popover-surface-width, auto);
    max-height: calc(100vh - 2rem);
    max-width: calc(100vw - 2rem);

    border-radius: var(--popover-border-radius, pxToRem(12));
    box-shadow: var(--shadow-depth-16);

    backdrop-filter: blur(pxToRem(5));

    &:after {
        // allows using `--popover-body-background-color` while
        // getting the blurred backdrop effect
        transition: opacity 0.4s ease;
        pointer-events: none;

        content: '';
        position: absolute;
        top: 0;
        right: 0;
        bottom: 0;
        left: 0;
        z-index: $limel-popover-before--z-index;

        opacity: 0.7;

        border-radius: var(--popover-border-radius, pxToRem(12));
        background-color: var(
            --popover-body-background-color,
            rgb(var(--contrast-100))
        );
    }

    &:focus,
    &:focus-within {
        outline: none;

        &:after {
            opacity: 1;
        }
    }

    &:focus-visible {
        // only when non-pointer input is being used,
        // e.g. tabbed into using keyboard
        box-shadow: var(--shadow-depth-16-focused);
    }
}

import { Component, Element, h, Prop, Watch } from '@stencil/core';
import { OpenDirection } from '../menu/menu.types';
import {
    createPopper,
    Instance,
    OptionsGeneric,
    Placement,
} from '@popperjs/core';
import { FlipModifier } from '@popperjs/core/lib/modifiers/flip';

/* eslint-disable jsdoc/check-indentation */
/**
 * The portal component provides a way to render children into a DOM node that
 * exist outside the DOM hierarchy of the parent component.
 *
 * There are some caveats when using this component
 *
 * * Events might not bubble up as expected since the content is moved out to
 *   another DOM node.
 * * Any styling that is applied to content from the parent will be lost, if the
 *   content is just another web compoent it will work without any issues.
 *   Alternatively, use the
 *   `style=""` html attribute.
 * * Any component that is placed inside the container must have a style of
 *   `max-height: inherit`. This ensures that its placement is calculated
 *   correctly in relation to the trigger, and that it never covers its own
 *   trigger.
 * * When the node is moved in the DOM, `disconnectedCallback` and
 *   `connectedCallback` will be invoked, so if `disconnectedCallback` is used
 *   to do any tear-down, the appropriate setup will have to be done again on
 *   `connectedCallback`.
 *
 * @slot - Content to put inside the portal
 * @private
 */
/* eslint-enable jsdoc/check-indentation */
@Component({
    tag: 'limel-portal',
    shadow: true,
    styleUrl: 'portal.scss',
})
export class Portal {
    /**
     * Decides which direction the portal content should open.
     */
    @Prop()
    public openDirection: OpenDirection = 'right';

    /**
     * Position of the content.
     */
    @Prop()
    public position: 'fixed' | 'absolute' = 'absolute';

    /**
     * A unique ID.
     */
    @Prop()
    public containerId: string;

    /**
     * Dynamic styling that can be applied to the container holding the content.
     */
    @Prop()
    public containerStyle: object = {};

    /**
     * Parent element to move the content to.
     */
    @Prop()
    public parent: HTMLElement = document.body;

    /**
     * Used to make a dropdown have the same width as the trigger, for example
     * in `limel-picker`.
     */
    @Prop()
    public inheritParentWidth = false;

    /**
     * True if the content within the portal should be visible.
     *
     * If the content is from within a dialog for instance, this can be set to
     * true from false when the dialog opens to position the content properly.
     */
    @Prop()
    public visible = false;

    @Watch('visible')
    protected onVisible() {
        if (!this.visible) {
            this.hideContainer();
            this.styleContainer();
            this.destroyPopper();

            return;
        }

        this.styleContainer();
        this.createPopper();
        requestAnimationFrame(() => {
            this.showContainer();
        });
    }

    @Element()
    private host: HTMLLimelPortalElement;

    private container: HTMLElement;

    private popperInstance: Instance;

    private loaded = false;

    private observer: IResizeObserver;

    public disconnectedCallback() {
        this.removeContainer();
        this.destroyPopper();
        if (this.observer) {
            this.observer.unobserve(this.container);
        }
    }

    public connectedCallback() {
        if (!this.loaded) {
            return;
        }

        this.createContainer();
        this.hideContainer();
        this.attachContainer();
        this.styleContainer();

        if (this.visible) {
            this.createPopper();
            this.showContainer();
        }

        if ('ResizeObserver' in window) {
            const observer = new ResizeObserver(() => {
                if (this.popperInstance) {
                    this.styleContainer();
                    this.popperInstance.update();
                }
            });
            observer.observe(this.container);
        }
    }

    public componentDidLoad() {
        this.loaded = true;
        this.connectedCallback();
    }

    public render() {
        return <slot />;
    }

    private createContainer() {
        const slot: HTMLSlotElement = this.host.shadowRoot.querySelector(
            'slot'
        );
        const content =
            (slot.assignedElements && slot.assignedElements()) || [];

        this.container = document.createElement('div');
        this.container.setAttribute('id', this.containerId);
        this.container.setAttribute('class', 'limel-portal--container');
        Object.assign(this.container, {
            portalSource: this.host,
        });

        content.forEach((element: HTMLElement) => {
            this.container.appendChild(element);
        });
    }

    private attachContainer() {
        this.parent.appendChild(this.container);
    }

    private removeContainer() {
        if (!this.container) {
            return;
        }

        this.hideContainer();
        this.container.parentElement.removeChild(this.container);
    }

    private hideContainer() {
        this.container.style.opacity = '0';
    }

    private showContainer() {
        this.container.style.opacity = '1';
    }

    private styleContainer() {
        const hostWidth = this.host.getBoundingClientRect().width;

        if (this.visible) {
            this.container.style.display = 'block';
        } else {
            this.container.style.display = 'none';
        }

        if (this.inheritParentWidth) {
            const containerWidth = this.getContentWidth(this.container);
            let width = containerWidth;
            if (hostWidth > 0) {
                width = hostWidth;
            }

            this.container.style.width = `${width}px`;
        }

        this.ensureContainerFitsInViewPort();

        Object.keys(this.containerStyle).forEach((property) => {
            this.container.style[property] = this.containerStyle[property];
        });
    }

    private getContentWidth(element: HTMLElement | Element) {
        if (!element) {
            return null;
        }

        const width = element.getBoundingClientRect().width;
        if (width !== 0) {
            return width;
        }

        const elementContent = element.querySelector('*');

        return this.getContentWidth(elementContent);
    }

    private createPopper() {
        const config = this.createPopperConfig();

        this.popperInstance = createPopper(this.host, this.container, config);
    }

    private destroyPopper() {
        this.popperInstance?.destroy();
        this.popperInstance = null;
    }

    private createPopperConfig(): Partial<
        OptionsGeneric<Partial<FlipModifier>>
    > {
        let placement: Placement = 'bottom-start';
        let flipPlacement: Placement = 'top-start';

        if (this.openDirection === 'left') {
            placement = 'bottom-end';
            flipPlacement = 'top-end';
        }

        return {
            strategy: this.position,
            placement: placement,
            modifiers: [
                {
                    name: 'flip',
                    options: {
                        fallbackPlacements: [flipPlacement],
                    },
                },
            ],
        };
    }

    private ensureContainerFitsInViewPort() {
        const viewHeight = Math.max(
            document.documentElement.clientHeight || 0,
            window.innerHeight || 0
        );

        const { top, bottom } = this.host.getBoundingClientRect();
        const spaceAboveTopOfSurface = Math.max(top, 0);
        const spaceBelowTopOfSurface = Math.max(viewHeight - bottom, 0);
        const extraCosmeticSpace = 16;
        const maxHeight =
            Math.max(spaceAboveTopOfSurface, spaceBelowTopOfSurface) -
            extraCosmeticSpace;

        this.container.style.maxHeight = `${maxHeight}px`;
    }
}

