import { ListItem, Option } from '@limetech/lime-elements';
import { MDCFloatingLabel } from '@limetech/mdc-floating-label';
import { MDCSelectHelperText } from '@limetech/mdc-select/helper-text';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { isMobileDevice } from '../../util/device';
import {
    ENTER,
    ENTER_KEY_CODE,
    SPACE,
    SPACE_KEY_CODE,
} from '../../util/keycodes';
import { isMultiple } from '../../util/multiple';
import { createRandomString } from '../../util/random-string';
import { SelectTemplate } from './select.template';

/**
 * @exampleComponent limel-example-select
 * @exampleComponent limel-example-select-with-icons
 * @exampleComponent limel-example-select-multiple
 * @exampleComponent limel-example-select-initially-empty
 * @exampleComponent limel-example-select-initially-empty-required
 * @exampleComponent limel-example-select-preselected
 * @exampleComponent limel-example-select-change-options
 * @exampleComponent limel-example-select-narrow
 * @exampleComponent limel-example-select-dialog
 */
@Component({
    tag: 'limel-select',
    shadow: true,
    styleUrl: 'select.scss',
})
export class Select {
    /**
     * Disables the select when `true`. Works exactly the same as
     * `readonly`. If either property is `true`, the select will be
     * disabled.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Disables the select when `true`. Works exactly the same as
     * `disabled`. If either property is `true`, the select will be
     * disabled.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Set to `true` to indicate that the current value of the select is
     * invalid.
     */
    @Prop({ reflect: true })
    public invalid: boolean;

    /**
     * True if the control requires a value.
     */
    @Prop({ reflect: true })
    public required = false;

    /**
     * Text to display next to the select.
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Optional helper text to display below the input field when it has focus.
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * Currently selected value or values (if `multiple` is set).
     */
    @Prop()
    public value: Option | Option[];

    /**
     * List of options.
     */
    @Prop()
    public options: Option[] = [];

    /**
     * Set to `true` to allow multiple values to be selected.
     */
    @Prop()
    public multiple: boolean = false;

    /**
     * Emitted when the value is changed.
     */
    @Event()
    private change: EventEmitter<Option | Option[]>;

    @Element()
    private host: HTMLLimelSelectElement;

    @State()
    private menuOpen: boolean = false;

    private hasChanged: boolean = false;
    private checkValid: boolean = false;
    private mdcSelectHelperText: MDCSelectHelperText;
    private mdcFloatingLabel: MDCFloatingLabel;
    private isMobileDevice: boolean;
    private portalId: string;

    constructor() {
        this.handleMenuChange = this.handleMenuChange.bind(this);
        this.handleNativeChange = this.handleNativeChange.bind(this);
        this.handleMenuTriggerKeyPress = this.handleMenuTriggerKeyPress.bind(
            this
        );
        this.openMenu = this.openMenu.bind(this);
        this.closeMenu = this.closeMenu.bind(this);

        this.portalId = createRandomString();
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentWillLoad() {
        this.isMobileDevice = isMobileDevice();

        // It should not be possible to render the native select for consumers, but we still want to make it testable.
        // We can set this attribute in tests to force rendering of the native select
        if (this.host.hasAttribute('data-native')) {
            this.isMobileDevice = true;
        }
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        let element: HTMLElement;
        element = this.host.shadowRoot.querySelector('.mdc-floating-label');
        if (!element) {
            return;
        }

        this.mdcFloatingLabel = new MDCFloatingLabel(element);

        element = this.host.shadowRoot.querySelector('.mdc-select-helper-text');
        if (element) {
            this.mdcSelectHelperText = new MDCSelectHelperText(element);
        }
    }

    public disconnectedCallback() {
        if (this.mdcFloatingLabel) {
            this.mdcFloatingLabel.destroy();
        }

        if (this.mdcSelectHelperText) {
            this.mdcSelectHelperText.destroy();
        }
    }

    public componentDidUpdate() {
        if (this.menuOpen) {
            this.setMenuFocus();
        }
    }

    public render() {
        const dropdownZIndex = getComputedStyle(this.host).getPropertyValue(
            '--dropdown-z-index'
        );

        return (
            <SelectTemplate
                id={this.portalId}
                disabled={this.disabled || this.readonly}
                required={this.required}
                invalid={this.invalid}
                label={this.label}
                helperText={this.helperText}
                value={this.value}
                options={this.options}
                onMenuChange={this.handleMenuChange}
                onNativeChange={this.handleNativeChange}
                onTriggerPress={this.handleMenuTriggerKeyPress}
                multiple={this.multiple}
                isOpen={this.menuOpen}
                open={this.openMenu}
                close={this.closeMenu}
                checkValid={this.checkValid}
                native={this.isMobileDevice}
                dropdownZIndex={dropdownZIndex}
            />
        );
    }

    @Watch('menuOpen')
    protected watchOpen(newValue: boolean, oldValue: boolean) {
        if (this.checkValid) {
            return;
        }

        // Menu was closed for the first time
        if (!newValue && oldValue) {
            this.checkValid = true;
        }
    }

    private setMenuFocus() {
        if (this.isMobileDevice) {
            return;
        }

        setTimeout(() => {
            const list: HTMLElement = document.querySelector(
                `#${this.portalId} limel-menu-surface limel-list`
            );
            const firstItem: HTMLElement = list?.shadowRoot?.querySelector(
                '[tabindex]'
            );

            if (firstItem) {
                firstItem.focus();
            }
        });
    }

    private setTriggerFocus() {
        const trigger: HTMLElement = this.host.shadowRoot.querySelector(
            '.limel-select-trigger'
        );
        trigger.focus();
    }

    private handleMenuChange(
        event: CustomEvent<Array<ListItem<Option>> | ListItem<Option>>
    ) {
        event.stopPropagation();

        if (isMultiple(event.detail)) {
            const listItems: ListItem[] = event.detail;
            const options: Option[] = listItems.map((item) => item.value);
            this.change.emit(options);

            return;
        }

        if (!event.detail.selected) {
            return;
        }

        const listItem: ListItem = event.detail;
        const option: Option = listItem.value;
        if (option.disabled) {
            return;
        }

        this.change.emit(option);
        this.menuOpen = false;
        this.setTriggerFocus();
    }

    private openMenu() {
        if (this.emitFirstChangeEvent()) {
            this.hasChanged = true;
            this.change.emit(this.options[0]);
        }

        this.menuOpen = true;
    }

    private emitFirstChangeEvent() {
        return !this.hasChanged && this.isMobileDevice && !this.value;
    }

    private closeMenu() {
        this.menuOpen = false;
    }

    private handleMenuTriggerKeyPress(event: KeyboardEvent) {
        const isEnter = event.key === ENTER || event.keyCode === ENTER_KEY_CODE;
        const isSpace = event.key === SPACE || event.keyCode === SPACE_KEY_CODE;

        if (!this.menuOpen && (isSpace || isEnter)) {
            event.stopPropagation();
            event.preventDefault();
            this.menuOpen = true;
        }
    }

    private handleNativeChange(event: Event) {
        event.stopPropagation();

        const element: HTMLSelectElement = this.host.shadowRoot.querySelector(
            'select.limel-select__native-control'
        );
        const options = Array.apply(null, element.options) // eslint-disable-line prefer-spread
            .filter((optionElement: HTMLOptionElement) => {
                return !!optionElement.selected;
            })
            .map((optionElement: HTMLOptionElement) => {
                return this.options.find(
                    (o) => o.value === optionElement.value
                );
            });

        if (this.multiple) {
            this.change.emit(options);

            return;
        }

        this.change.emit(options[0]);
        this.menuOpen = false;
    }
}

@import '../../style/internal/mdc-variables';
@import '../../style/internal/shared_input-select-picker';

@import '@limetech/mdc-select/mdc-select';
@import '@limetech/mdc-select/helper-text/mdc-select-helper-text';
@import '@limetech/mdc-floating-label/mdc-floating-label';

@import '../../style/mixins';

// Note! The `--dropdown-z-index` property is used from `select.tsx`.
/**
 * @prop --dropdown-z-index: z-index of the dropdown menu.
 */

:host {
    display: block;
    position: relative;
    height: $height-of-mdc-text-field;
}

:host([hidden]) {
    display: none;
}

.limel-select__selected-option {
    display: flex;
    align-items: center;

    box-sizing: border-box;
    outline: none;

    padding: 0 pxToRem(16);
    align-self: center;
    min-width: 0; // makes it truncate and prevents the select to grow wider than its container
}

.limel-select__selected-option__icon {
    margin-right: pxToRem(8);
    flex-shrink: 0;
}

.limel-select__selected-option__text {
    @include looks-like-input-label;
    @include truncate-text;
}

.limel-select {
    height: 100%;

    .limel-select-trigger {
        display: inline-flex;
        align-items: center;
        height: 100%;

        cursor: pointer;
        border-radius: pxToRem(5);
        background-color: $background-color-focused;

        &:focus {
            outline: none;

            .mdc-floating-label {
                color: var(--mdc-theme-primary);
            }
        }
        &:focus-visible {
            box-shadow: var(--shadow-depth-8-focused) !important;
        }

        .mdc-floating-label {
            @include looks-like-input-label;
            top: 50%;
            right: pxToRem(
                16
            ); //This is a hack to force the label truncate when container is too little. Otherwise due to position: absolute it won't. Kia
            transform: translateY(-50%);

            &.mdc-floating-label--float-above {
                @include label--float-above;
            }

            &.mdc-floating-label--active {
                color: var(--mdc-theme-primary);
            }
        }
    }

    .mdc-select__dropdown-icon {
        transition: box-shadow 0.2s ease;
        border-radius: 50%;
        background: url("data:image/svg+xml;charset=utf-8,%3Csvg width='10' height='5' viewBox='7 10 10 5' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath fill-rule='evenodd' opacity='.54' d='M7 10l5 5 5-5z'/%3E%3C/svg%3E")
            no-repeat 50%;

        position: relative;
        bottom: unset;
    }

    .limel-select-trigger,
    .limel-select__selected-option {
        width: 100%;
    }

    &.mdc-select--disabled {
        .limel-select-trigger {
            @include looks-disabled;
        }
    }

    &:not(.mdc-select--disabled) {
        .limel-select-trigger {
            @include is-elevated-clickable;

            &.limel-select--focused {
                background-color: $background-color-focused;

                .mdc-floating-label {
                    color: var(--mdc-theme-primary);
                }

                .mdc-select__dropdown-icon {
                    transform: rotate(180deg);
                    color: var(--mdc-theme-primary);
                }
            }
        }
    }

    &.limel-select--required {
        .mdc-floating-label::after {
            content: '*';
        }
    }

    &.limel-select--invalid {
        .limel-select-trigger .mdc-floating-label,
        .mdc-select-helper-text {
            color: $mdc-theme-error;
        }
    }
}

.mdc-select-helper-line {
    @include looks-like-helper-line;
}
.mdc-select-helper-text {
    &:before {
        @include looks-like-helper-text-pseudo-before;
    }
}

select.limel-select__native-control {
    position: absolute;
    top: 0;
    left: 0;

    width: 100%;
    height: 100%;
    opacity: 0;
    border: 0;
}

:host(.is-narrow) {
    height: pxToRem(36);

    .mdc-floating-label {
        &.mdc-floating-label--float-above {
            transform: translateY(-1.76rem) scale(0.75);
        }
    }

    .limel-select__selected-option {
        padding: 0 pxToRem(12);
    }
}

import { MDCSlider } from '@limetech/mdc-slider';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    State,
    Watch,
} from '@stencil/core';
import { getPercentageClass } from './getPercentageClass';
/**
 * @exampleComponent limel-example-slider
 * @exampleComponent limel-example-slider-multiplier
 * @exampleComponent limel-example-slider-multiplier-percentage-colors
 */
@Component({
    tag: 'limel-slider',
    shadow: true,
    styleUrl: 'slider.scss',
})
export class Slider {
    /**
     * Disables the slider when `true`. Works exactly the same as
     * `readonly`. If either property is `true`, the slider will be
     * disabled.
     */
    @Prop({ reflect: true })
    public disabled = false;

    /**
     * Disables the slider when `true`. Works exactly the same as
     * `disabled`. If either property is `true`, the slider will be
     * disabled.
     */
    @Prop({ reflect: true })
    public readonly = false;

    /**
     * Default value: 1.
     * The factor that the properties `value`, `valuemax` and `valuemin` are multiplied by.
     * On `change` divides the value by the factor, so the original format stays the same
     */
    @Prop({ reflect: true })
    public factor: number = 1;

    /**
     * Label to display next to the input
     */
    @Prop({ reflect: true })
    public label: string;

    /**
     * Optional helper text to display below the slider
     */
    @Prop({ reflect: true })
    public helperText: string;

    /**
     * Unit to display next to the value
     */
    @Prop({ reflect: true })
    public unit: string = '';

    /**
     * The value of the input
     */
    @Prop({ reflect: true })
    public value: number;

    /**
     * The maximum value allowed
     */
    @Prop({ reflect: true })
    public valuemax: number = 100; // eslint-disable-line no-magic-numbers

    /**
     * The minimum value allowed
     */
    @Prop({ reflect: true })
    public valuemin: number = 0;

    /**
     * The stepping interval to use when adjusting the value
     */
    @Prop({ reflect: true })
    public step: number;

    /**
     * Emitted when the value has been changed
     */
    @Event()
    private change: EventEmitter<number>;

    @Element()
    private rootElement: HTMLLimelSliderElement;

    private mdcSlider: MDCSlider;

    @State()
    private percentageClass: string;

    public constructor() {
        this.inputHandler = this.inputHandler.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.rootElement.shadowRoot.querySelector(
            '.mdc-slider'
        );
        if (!element) {
            return;
        }

        this.mdcSlider = new MDCSlider(element);
        this.mdcSlider.listen('MDCSlider:change', this.changeHandler);
        this.mdcSlider.listen('MDCSlider:input', this.inputHandler);
    }

    public componentWillLoad() {
        this.setPercentageClass(this.value);
    }

    public componentWillUpdate() {
        this.mdcSlider.disabled = this.disabled || this.readonly;
    }

    public disconnectedCallback() {
        this.mdcSlider.unlisten('MDCSlider:change', this.changeHandler);
        this.mdcSlider.unlisten('MDCSlider:input', this.inputHandler);
        this.mdcSlider.destroy();
    }

    public render() {
        return (
            <div class={`slider ${this.percentageClass}`}>
                <label class="slider__label mdc-floating-label mdc-floating-label--float-above">
                    {this.label}
                </label>
                <div class="slider__content">
                    <div class="slider__content-range-container">
                        <span class="slider__content-min-label">
                            {this.multiplyByFactor(this.valuemin)}
                            {this.unit}
                        </span>
                        <span class="slider__content-max-label">
                            {this.multiplyByFactor(this.valuemax)}
                            {this.unit}
                        </span>
                    </div>
                    <div
                        class="mdc-slider mdc-slider--discrete"
                        role="slider"
                        aria-valuemin={this.multiplyByFactor(this.valuemin)}
                        aria-valuemax={this.multiplyByFactor(this.valuemax)}
                        aria-valuenow={this.multiplyByFactor(this.getValue())}
                        aria-label={this.label}
                        aria-disabled={this.disabled || this.readonly}
                        data-step={this.step}
                    >
                        <div class="mdc-slider__track-container">
                            <div class="mdc-slider__track" />
                        </div>
                        <div class="mdc-slider__thumb-container">
                            <div class="mdc-slider__pin">
                                <span class="mdc-slider__pin-value-marker">
                                    {this.multiplyByFactor(this.getValue())}
                                </span>
                            </div>
                            <svg
                                class="mdc-slider__thumb"
                                width="21"
                                height="21"
                            >
                                <circle cx="10.5" cy="10.5" r="7.875" />
                            </svg>
                            <div class="mdc-slider__focus-ring" />
                        </div>
                    </div>
                </div>
                {this.renderHelperLine()}
            </div>
        );
    }

    private renderHelperLine() {
        if (!this.helperText) {
            return;
        }

        return (
            <div class="mdc-slider-helper-line">
                <p class="mdc-slider-helper-text">{this.helperText}</p>
            </div>
        );
    }

    @Watch('value')
    protected watchValue() {
        if (!this.mdcSlider) {
            return;
        }

        this.mdcSlider.value = this.multiplyByFactor(this.getValue());
    }

    private changeHandler = (event) => {
        this.change.emit(event.detail.value / this.factor);
    };

    private multiplyByFactor(value) {
        return Math.round(value * this.factor);
    }

    private getValue() {
        let value = this.value;
        if (!isFinite(value)) {
            value = this.valuemin;
        }

        return value;
    }

    private inputHandler(event) {
        this.setPercentageClass(event.detail.value / this.factor);
    }

    private setPercentageClass(value) {
        this.percentageClass = getPercentageClass(
            (value - this.valuemin) / (this.valuemax - this.valuemin)
        );
    }
}

@import '../../style/internal/mdc-variables';
@import '../../style/internal/shared_input-select-picker';
@import './partial-styles/percentage-color.scss';

@import '@limetech/mdc-slider/mdc-slider';
@import '@limetech/mdc-floating-label/mdc-floating-label';

/**
 * @prop --slider-bg-color-behind-component: Color of "transparent" parts of disabled slider. Defaults to #ffffff.
 */

$colorGrey: #999999;

:host {
    --mdc-slider-bg-color-behind-component: var(
        --slider-bg-color-behind-component,
        #ffffff
    );
}

.mdc-slider {
    @include mdc-slider-highlight-color(
        var(--lime-primary-color, $mdc-theme-primary)
    );
    @include mdc-slider-thumb-color(
        var(--lime-primary-color, $mdc-theme-primary)
    );
    @include mdc-slider-value-pin-fill-color(
        var(--lime-primary-color, $mdc-theme-primary)
    );
    @include mdc-slider-value-pin-ink-color(on-primary);
}

.mdc-slider__pin-value-marker {
    margin-bottom: pxToRem(2);
}

.slider {
    position: relative;
    padding-top: pxToRem(
        20
    ); // Without this, the slider's label will be rendered outside the slider's box model (because it has an absolute position and transform)
}

.slider__label {
    @include looks-like-input-label;
    padding-left: pxToRem(20);
    font-size: pxToRem(16);
}

.slider__content {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    margin: pxToRem(-2) 0 pxToRem(2) 0;
}

.slider__content-range-container {
    display: flex;
    order: 2;
    justify-content: space-between;
    width: calc(100% - #{pxToRem(8)});
    margin: 0 auto;
    padding-top: pxToRem(4);
}

.slider__content-min-label,
.slider__content-max-label {
    opacity: 0.8;
    font-size: pxToRem(12);
    top: pxToRem(20);

    &:hover {
        opacity: 1;
    }
}

.slider__content-min-label {
    left: 0;
}

.slider__content-max-label {
    right: 0;
}

.mdc-slider {
    width: calc(100% - #{pxToRem(32)});
    margin: 0 auto;
    height: pxToRem(12);

    &:before,
    &:after {
        content: '';
        display: inline-block;
        position: absolute;
        top: pxToRem(7);

        width: pxToRem(6);
        height: pxToRem(6);
        border-radius: 50%;
        background-color: rgb(var(--contrast-600));
    }

    &:before {
        left: pxToRem(-12);
    }

    &:after {
        right: pxToRem(-12);
    }
}

.mdc-slider__track,
.mdc-slider__pin {
    transition: background-color 0.5s ease;
}

.mdc-slider__track-container {
    top: pxToRem(8);
    height: pxToRem(4);
    border-radius: pxToRem(4);
}

.mdc-slider__thumb-container {
    top: 0;
    z-index: $limel-slider--thumb-container--z-index;

    .mdc-slider:not(.mdc-slider--disabled) & {
        &:before,
        &:after {
            transition: all 0.2s ease 0.2s;
            content: '';
            display: block;
            position: absolute;
            top: pxToRem(8);
            bottom: 0;
            margin: auto;
            background: {
                image: url("data:image/svg+xml;charset=utf-8,%3Csvg width='10' height='5' viewBox='7 10 10 5' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath fill-rule='evenodd' opacity='.54' d='M7 10l5 5 5-5z'/%3E%3C/svg%3E");
                position: center;
                repeat: no-repeat;
                size: cover;
            }

            width: pxToRem(12);
            opacity: 0;
        }

        &:before {
            left: pxToRem(-16);
            transform: rotate(90deg);
        }
        &:after {
            right: pxToRem(-14);
            transform: rotate(-90deg);
        }

        &:hover {
            &:before {
                opacity: 0.8;
                left: pxToRem(-20);
            }
            &:after {
                opacity: 0.8;
                right: pxToRem(-18);
            }
        }
    }
}

.mdc-slider__pin {
    transition: all 0.2s ease;
    transform: none;
    border-radius: pxToRem(20);

    height: pxToRem(20);
    line-height: pxToRem(20);
    padding: 0 pxToRem(2);

    left: pxToRem(-4);
    margin: 0 auto;

    transform-origin: bottom;
    .mdc-slider:not(.mdc-slider--disabled) & {
        box-shadow: var(--button-shadow-normal);

        &:hover {
            box-shadow: var(--button-shadow-hovered);
        }
    }

    .mdc-slider--active & {
        // when the pin is being dragged
        box-shadow: var(--button-shadow-hovered);
        transform: scale(1.5) translateY(pxToRem(-16)) !important;
    }

    .mdc-slider.mdc-slider--disabled & {
        background: var(--mdc-slider-bg-color-behind-component);
    }
}

.mdc-slider__thumb {
    box-shadow: 0 0 0 pxToRem(2) rgba(var(--contrast-100), 0.8),
        0 0 0 pxToRem(3) rgba(var(--contrast-100), 0.8) inset;
    border-radius: 50%;
}

.mdc-slider__pin-value-marker {
    transition: color 0.5s ease;
    font-size: pxToRem(12);
    transform: rotate(0deg);
    margin-bottom: 0;
}

.mdc-slider--discrete {
    &.mdc-slider--active {
        .mdc-slider__pin {
            transform: none;
        }
        .mdc-slider__thumb {
            transform: scale3d(1, 1, 1);
        }
    }
}

.mdc-slider-helper-line {
    @include looks-like-helper-line;
}
.mdc-slider-helper-text {
    @include looks-like-helper-text;
    opacity: 1;
    &:before {
        @include looks-like-helper-text-pseudo-before;
    }
}

import { MDCSnackbar, MDCSnackbarCloseEvent } from '@limetech/mdc-snackbar';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Method,
    Prop,
} from '@stencil/core';

/**
 * @exampleComponent limel-example-snackbar
 */
@Component({
    tag: 'limel-snackbar',
    shadow: true,
    styleUrl: 'snackbar.scss',
})
export class Snackbar {
    /**
     * The text message to display.
     */
    @Prop()
    public message: string;

    /**
     * The amount of time in milliseconds to show the snackbar.
     */
    @Prop()
    public timeout: number;

    /**
     * The text to display for the action button.
     */
    @Prop()
    public actionText: string;

    /**
     * True if the snackbar is dismissible, false otherwise
     */
    @Prop()
    public dismissible: boolean;

    /**
     * Whether to show the snackbar with space for multiple lines of text
     */
    @Prop()
    public multiline: boolean;

    @Element()
    private host: HTMLLimelSnackbarElement;

    /**
     * Emitted when the action button is pressed
     */
    @Event()
    private action: EventEmitter<void>;

    /**
     * Emitted when the snackbar hides itself
     */
    @Event()
    private hide: EventEmitter<void>;

    private mdcSnackbar: MDCSnackbar;

    constructor() {
        this.handleMdcClosing = this.handleMdcClosing.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.host.shadowRoot.querySelector('.mdc-snackbar');
        if (!element) {
            return;
        }

        this.mdcSnackbar = new MDCSnackbar(element);

        this.mdcSnackbar.listen('MDCSnackbar:closing', this.handleMdcClosing);
    }

    public disconnectedCallback() {
        this.mdcSnackbar.unlisten('MDCSnackbar:closing', this.handleMdcClosing);
        this.mdcSnackbar.destroy();
    }

    /**
     * Show the snackbar
     */
    @Method()
    public async show() {
        if (this.timeout) {
            this.mdcSnackbar.timeoutMs = this.timeout;
        }

        this.mdcSnackbar.labelText = this.message;

        this.mdcSnackbar.open();
    }

    public render() {
        return (
            <div
                class={`
                    mdc-snackbar
                    ${this.multiline ? 'mdc-snackbar--stacked' : ''}
                `}
            >
                <div class="mdc-snackbar__surface">
                    <div
                        class="mdc-snackbar__label"
                        role="status"
                        aria-live="polite"
                    ></div>
                    {this.renderActions(this.actionText, this.dismissible)}
                </div>
            </div>
        );
    }

    private handleMdcClosing(event: MDCSnackbarCloseEvent) {
        if (event.detail.reason === 'action') {
            this.action.emit();
        } else {
            this.hide.emit();
        }
    }

    private renderActions(actionText: string, dismissible: boolean) {
        if (!actionText && !dismissible) {
            return;
        }

        return (
            <div class="mdc-snackbar__actions">
                {this.renderActionButton(actionText)}
                {this.renderDismissButton(dismissible)}
            </div>
        );
    }

    private renderActionButton(actionText: string) {
        if (!actionText) {
            return;
        }

        return (
            <button type="button" class="mdc-button mdc-snackbar__action">
                <span class="mdc-button__label">{actionText}</span>
            </button>
        );
    }

    private renderDismissButton(dismissible: boolean) {
        if (!dismissible) {
            return;
        }

        const svgData = `<svg width="32" height="32" x="0px" y="0px" viewBox="0 0 32 32" xmlns="http://www.w3.org/2000/svg" xmlns:svg="http://www.w3.org/2000/svg">
    <line fill="none" id="svg_1" stroke="currentColor" stroke-width="2" x1="8" x2="24" y1="8" y2="24"/>
    <line fill="none" id="svg_2" stroke="currentColor" stroke-width="2" x1="24" x2="8" y1="8" y2="24"/>
</svg>`;

        return (
            <div class="mdc-snackbar__dismiss">
                <button class="mdc-icon-button" innerHTML={svgData} />
            </div>
        );
    }
}

