import { MDCDialog } from '@material/dialog';
import {
    Component,
    Element,
    Event,
    EventEmitter,
    h,
    Prop,
    Watch,
} from '@stencil/core';
import { dispatchResizeEvent } from '../../util/dispatch-resize-event';
import { createRandomString } from '../../util/random-string';

/**
 * :::note
 * Regarding the `close` event: When putting other elements that emit `close`
 * events inside a dialog, those events must be caught and stopped inside the
 * dialog. If not, they will bubble to the event handler listening for `close`
 * events on the dialog, which will close the dialog too.
 *
 * See the example _Nested `close` events_.
 * :::
 *
 * @exampleComponent limel-example-dialog
 * @exampleComponent limel-example-dialog-nested-close-events
 * @exampleComponent limel-example-dialog-heading
 * @exampleComponent limel-example-dialog-form
 * @exampleComponent limel-example-dialog-size
 * @exampleComponent limel-example-dialog-fullscreen
 * @exampleComponent limel-example-dialog-closing-actions
 * @exampleComponent limel-example-dialog-action-buttons
 * @slot - Content to put inside the dialog
 * @slot button - The dialog buttons
 */
@Component({
    tag: 'limel-dialog',
    shadow: true,
    styleUrl: 'dialog.scss',
})
export class Dialog {
    /**
     * The heading for the dialog, if any.
     */
    @Prop()
    public heading: string | DialogHeading;

    /**
     * Set to `true` to make the dialog "fullscreen".
     */
    @Prop({ reflect: true })
    public fullscreen = false;

    /**
     * `true` if the dialog is open, `false` otherwise.
     */
    @Prop({ mutable: true, reflect: true })
    public open = false;

    /**
     * Defines which action triggers a close-event.
     */
    @Prop({ reflect: true })
    public closingActions: { escapeKey: boolean; scrimClick: boolean } = {
        escapeKey: true,
        scrimClick: true,
    };
    /**
     * Emitted when the dialog is closed from inside the component.
     * (*Not* emitted when the consumer sets the `open`-property to `false`.)
     */
    @Event()
    private close: EventEmitter<void>;

    /**
     * Emitted when the dialog is in the process of being closed.
     */
    @Event()
    private closing: EventEmitter<void>;

    @Element()
    private host: HTMLLimelDialogElement;

    private mdcDialog: MDCDialog;

    private id: string;

    private showFooter = true;

    constructor() {
        this.handleMdcOpened = this.handleMdcOpened.bind(this);
        this.handleMdcClosed = this.handleMdcClosed.bind(this);
        this.handleMdcClosing = this.handleMdcClosing.bind(this);
    }

    public connectedCallback() {
        this.initialize();
    }

    public componentWillLoad() {
        this.id = createRandomString();
        this.showFooter = !!this.host.querySelector('[slot="button"]');
    }

    public componentDidLoad() {
        this.initialize();
    }

    private initialize() {
        const element = this.host.shadowRoot.querySelector('.mdc-dialog');
        if (!element) {
            return;
        }

        this.mdcDialog = new MDCDialog(element);
        if (this.open) {
            this.mdcDialog.open();
        }

        this.mdcDialog.listen('MDCDialog:opened', this.handleMdcOpened);
        this.mdcDialog.listen('MDCDialog:closed', this.handleMdcClosed);
        this.mdcDialog.listen('MDCDialog:closing', this.handleMdcClosing);

        this.mdcDialog.scrimClickAction = '';
        if (this.closingActions.scrimClick) {
            this.mdcDialog.scrimClickAction = 'close';
        }

        this.mdcDialog.escapeKeyAction = '';
        if (this.closingActions.escapeKey) {
            this.mdcDialog.escapeKeyAction = 'close';
        }
    }

    public disconnectedCallback() {
        this.mdcDialog.unlisten('MDCDialog:opened', this.handleMdcOpened);
        this.mdcDialog.unlisten('MDCDialog:closed', this.handleMdcClosed);
        this.mdcDialog.unlisten('MDCDialog:closing', this.handleMdcClosing);
        this.mdcDialog.destroy();
    }

    public render() {
        return (
            <div
                class={{
                    'mdc-dialog': true,
                    'full-screen': !!this.fullscreen,
                }}
                role="alertdialog"
                aria-modal="true"
                aria-labelledby={'limel-dialog-title-' + this.id}
                aria-describedby={'limel-dialog-content-' + this.id}
            >
                <input hidden={true} id="initialFocusEl" />
                <div class="mdc-dialog__container">
                    <div class="mdc-dialog__surface">
                        {/*
                            The `initialFocusElement` below is needed to make
                            focus trapping work. At the time of writing, the
                            focusable elements inside the slots are not
                            detected, so we supply our own hidden element for
                            the focus trap to use. Read more here:
                            https://github.com/material-components/material-components-web/tree/v11.0.0/packages/mdc-dialog#handling-focus-trapping
                        */}
                        <input type="text" id="initialFocusElement" />
                        {this.renderHeading()}
                        <div
                            class="mdc-dialog__content scrollbox"
                            id={'limel-dialog-content-' + this.id}
                        >
                            <slot />
                        </div>
                        {this.renderFooter()}
                    </div>
                </div>
                <div class="mdc-dialog__scrim" />
            </div>
        );
    }

    @Watch('open')
    protected watchHandler(newValue: boolean, oldValue: boolean) {
        if (oldValue === newValue) {
            return;
        }

        if (!this.mdcDialog) {
            return;
        }

        if (newValue) {
            this.mdcDialog.open();
        } else {
            this.mdcDialog.close();
        }
    }

    private handleMdcOpened() {
        // When the opening-animation has completed, dispatch a
        // resize-event so that any content that depends on
        // javascript for layout has a chance to update to the
        // final layout of the dialog. /Ads
        const waitForUiToRender = 100;
        setTimeout(dispatchResizeEvent, waitForUiToRender);
    }

    private handleMdcClosed() {
        if (this.open) {
            this.close.emit();
        }

        this.open = false;
    }

    private handleMdcClosing() {
        this.closing.emit();
    }

    private isBadgeHeading(
        heading: string | DialogHeading
    ): heading is DialogHeading {
        return typeof heading === 'object' && !!heading.title && !!heading.icon;
    }

    private renderHeading() {
        if (this.isBadgeHeading(this.heading)) {
            const { title, subtitle, supportingText, icon } = this.heading;

            return (
                <limel-header
                    icon={icon}
                    heading={title}
                    subheading={subtitle}
                    supportingText={supportingText}
                ></limel-header>
            );
        } else if (typeof this.heading === 'string') {
            return <limel-header heading={this.heading}></limel-header>;
        }

        return null;
    }

    private renderFooter() {
        if (this.showFooter) {
            return (
                <footer class="mdc-dialog__actions">
                    <slot name="button" />
                </footer>
            );
        }
    }
}

@use '../../style/functions';

@use '../../style/internal/lime-theme';

@use '@material/dialog';

/**
 * @prop --dialog-width: Width of the dialog.
 * @prop --dialog-height: Height of the dialog.
 * @prop --dialog-z-index: z-index of the dialog container, including the scrim.
 * @prop --dialog-heading-title-color: Color of the title.
 * @prop --dialog-heading-subtitle-color: Color of the subtitle.
 * @prop --dialog-heading-supporting-text-color: Color of the supporting text.
 * @prop --dialog-heading-icon-color: Color of the icon.
 * @prop --dialog-heading-icon-background-color: Background color of the icon when displayed as a badge.
 */

:host {
    --dialog-background-color: rgb(var(--contrast-100));
    --header-heading-color: var(--dialog-heading-title-color);
    --header-subheading-color: var(--dialog-heading-subtitle-color);
    --header-supporting-text-color: var(--dialog-heading-supporting-text-color);
    --header-icon-color: var(--dialog-heading-icon-color);
    --header-icon-background-color: var(--dialog-heading-icon-background-color);
}

@include dialog.core-styles;

.mdc-dialog {
    @include dialog.max-width(16000px, dialog.$margin);

    z-index: var(--dialog-z-index, dialog.$z-index);

    &.full-screen {
        @include dialog.max-height(16000px, dialog.$margin);
        .mdc-dialog__container {
            height: 100%;
            width: 100%;

            .mdc-dialog__surface {
                height: 100%;
                width: 100%;
            }
        }
    }
    .mdc-dialog__container {
        height: 100%;
    }

    .mdc-dialog__surface {
        width: var(--dialog-width, auto);
        height: var(--dialog-height, auto);
        background-color: var(--dialog-background-color);
        box-shadow: var(--shadow-depth-64);
    }
}

.scrollbox {
    --dialog-background-color-transparent: rgba(var(--contrast-100), 0);
    --dialog-scroll-shadow-color: rgba(var(--color-black), 0.2);
    --dialog-scroll-shadow-color-transparent: rgba(var(--color-black), 0);

    // Credit to the ever fantastic Lea Verou!
    // http://lea.verou.me/2012/04/background-attachment-local/

    /* prettier-ignore */
    background:
        /* Shadow covers */
        linear-gradient(
            var(--dialog-background-color) 30%,
            var(--dialog-background-color-transparent)
        ),
        linear-gradient(
            var(--dialog-background-color-transparent),
            var(--dialog-background-color) 70%
        )
        0 100%,

        /* Shadows */
        radial-gradient(
            farthest-side at 50% 0,
            var(--dialog-scroll-shadow-color),
            var(--dialog-scroll-shadow-color-transparent)
        ),
        radial-gradient(
            farthest-side at 50% 100%,
            var(--dialog-scroll-shadow-color),
            var(--dialog-scroll-shadow-color-transparent)
        )
        0 100%;

    background-repeat: no-repeat;
    background-color: var(--dialog-background-color);
    background-size: 100% functions.pxToRem(40), 100% functions.pxToRem(40),
        100% functions.pxToRem(14), 100% functions.pxToRem(14);

    /* Opera doesn't support this in the shorthand */
    background-attachment: local, local, scroll, scroll;
}

#initialFocusElement {
    position: absolute;
    opacity: 0;
    pointer-events: none;
    z-index: -1;
}

slot[name='header'] {
    display: none;
}

slot[name='button'] {
    display: flex;
    gap: 0.5rem; // Makes sure buttons get some default distance
    width: 100%;
    justify-content: flex-end;
}

@media screen and (max-width: 760px) {
    slot[name='button'] {
        flex-direction: column-reverse;
    }
}

@import '../../_generic-example-styles/_do-dont';

// We don't want the consumers to see these styles,
// as they are just to visualize the fake examples, thus distracting.
* {
    box-sizing: border-box;
}

.example {
    margin: 0.5rem 0;
    background-color: rgb(var(--contrast-600));
}

.fake-dialog-container {
    --header-top-right-left-border-radius: 0.25rem;
    width: 100%;
    height: 100%;
    max-width: 40rem;
    padding: 0 3rem 3rem 3rem;
    margin: auto;
    background-color: rgb(var(--contrast-600));

    &.shows-full-dialog {
        padding-top: 3rem;
    }

    p {
        font-size: 0.875rem;
        opacity: 0.7;
        align-self: flex-start;
        padding: 0 0.75rem;
    }

    h4 {
        padding: 0 0.75rem;
        margin-bottom: 0;
    }
}

.action-bar {
    display: flex;
    gap: 0.5rem;
    width: 100%;
    justify-content: flex-end;
    padding: 0.5rem;
}

.fake-dialog {
    display: flex;
    flex-direction: column;
    min-height: 3rem;
    background-color: rgb(var(--contrast-100));
    box-shadow: var(--shadow-depth-16);
    border-radius: 0 0 0.25rem 0.25rem;

    .shows-full-dialog & {
        border-radius: 0.25rem;
    }
}

limel-header {
    &.save {
        --header-icon-color: rgb(var(--color-lime-default));
    }
    &.delete {
        --header-icon-color: rgb(var(--color-red-default));
    }
}

.button {
    &.justify-left {
        justify-self: flex-start;
        margin-right: auto;
    }
    &.primary--neutral {
        --lime-primary-color: rgb(var(--contrast-1100));
    }
    &.primary--caution {
        --lime-on-primary-color: rgb(var(--color-gray-darker));
        --lime-primary-color: rgb(var(--color-amber-default));
    }
    &.primary--danger {
        --lime-on-primary-color: rgb(var(--color-red-default));
        --lime-primary-color: rgb(var(--contrast-100));
    }
    &.primary--danger-highlighted {
        --lime-primary-color: rgb(var(--color-red-default));
    }
}

