import { Chip } from '@limetech/lime-elements';
import { Component, h, State } from '@stencil/core';
const CHIP_SELECET_ALL_ID = 1;

/**
 * Filter chip set with badge.
 * The badge can be used to visulize the number of results using each filter.
 */
@Component({
    tag: 'limel-example-chip-set-filter-badge',
    shadow: true,
})
export class ChipSetFilterBadgeExample {
    @State()
    private chips: Chip[] = [
        {
            id: 1,
            text: 'All',
        },
        {
            id: 2,
            text: 'Lime',
            badge: 5,
        },
        {
            id: 3,
            text: 'Apple',
            selected: true,
            badge: 0,
        },
        {
            id: 4,
            text: 'Banana',
            badge: 1,
        },
    ];

    @State()
    private disabled: boolean = false;

    constructor() {
        this.setAllBadgeValue();
    }

    public render() {
        return [
            <limel-chip-set
                label="Include fruits of type:"
                disabled={this.disabled}
                type="filter"
                onChange={this.handleChange}
                value={this.chips}
            />,
            <p>
                <limel-flex-container justify="end">
                    <limel-checkbox
                        label="Disabled"
                        onChange={this.setDisabled}
                        checked={this.disabled}
                    />
                </limel-flex-container>
            </p>,
        ];
    }

    private handleChange = (event: CustomEvent<Chip>) => {
        const updatedChips = [...this.chips];
        if (event.detail.id !== CHIP_SELECET_ALL_ID) {
            updatedChips[0].selected = false;
        }

        if (event.detail.id === CHIP_SELECET_ALL_ID && event.detail.selected) {
            updatedChips.forEach((chip) => {
                chip.selected = false;
            });
        }

        const index = this.chips.findIndex(
            (chips) => chips.id === event.detail.id
        );
        updatedChips[index] = event.detail;
        this.chips = updatedChips;
    };

    private setDisabled = (event: CustomEvent<boolean>) => {
        this.disabled = event.detail;
    };

    private setAllBadgeValue() {
        let badgeValue = 0;
        this.chips.forEach((chip) => {
            if (chip.id !== CHIP_SELECET_ALL_ID) {
                badgeValue += chip.badge;
            }
        });
        this.chips[0].badge = badgeValue;
    }
}

import { Chip } from '@limetech/lime-elements';
import { Component, h, State } from '@stencil/core';
import { ENTER, ENTER_KEY_CODE } from '../../../util/keycodes';

/**
 * Input chip set
 *
 * Useful for collections of tags or labels. Can also be used as an advanced
 * search input, with leading icon and a delimiter between search terms.
 */
@Component({
    tag: 'limel-example-chip-set-input',
    shadow: true,
    styleUrl: 'chip-set-input.scss',
})
export class ChipSetInputExample {
    @State()
    private value: Chip[];

    @State()
    private textValue = '';

    @State()
    private required: boolean = false;

    @State()
    private readonly: boolean = false;

    @State()
    private disabled: boolean = false;

    @State()
    private maxItems = 0;

    @State()
    private emptyInputOnBlur: boolean = true;

    @State()
    private hasLeadingIcon: boolean = false;

    @State()
    private delimiter: string = null;

    constructor() {
        this.value = [
            this.createChip('Elephant'),
            this.createChip('Caterpillar'),
            this.createChip('Badger'),
            this.createChip('Fish'),
        ];
    }

    public render() {
        return [
            <limel-flex-container align="end">
                <limel-chip-set
                    label="Animal"
                    type="input"
                    value={this.value}
                    required={this.required}
                    readonly={this.readonly}
                    disabled={this.disabled}
                    leadingIcon={this.hasLeadingIcon ? 'search' : null}
                    maxItems={this.maxItems}
                    onChange={this.handleChange}
                    onInput={this.handleInput}
                    onInteract={this.handleInteraction}
                    onKeyUp={this.onKeyUp}
                    searchLabel="Add an animal"
                    emptyInputOnBlur={this.emptyInputOnBlur}
                    delimiter={this.delimiter}
                />
                <limel-input-field
                    label="Max items"
                    value={this.maxItems.toString()}
                    type="number"
                    onChange={this.setMaxItems}
                />
            </limel-flex-container>,
            <p>
                <limel-flex-container justify="end">
                    <limel-checkbox
                        label="Empty input on blur"
                        onChange={this.setEmptyInputOnBlur}
                        checked={this.emptyInputOnBlur}
                    />
                    <limel-checkbox
                        label="Disabled"
                        onChange={this.setDisabled}
                        checked={this.disabled}
                    />
                    <limel-checkbox
                        label="Readonly"
                        onChange={this.setReadonly}
                        checked={this.readonly}
                    />
                    <limel-checkbox
                        label="Required"
                        onChange={this.setRequired}
                        checked={this.required}
                    />
                    <limel-checkbox
                        label={'Leading icon'}
                        onChange={this.setLeadingIcon}
                        checked={this.hasLeadingIcon}
                    />
                    <limel-checkbox
                        label="Use delimiters"
                        onChange={this.useDelimiters}
                        checked={this.delimiter !== null}
                    />
                </limel-flex-container>
            </p>,
            <limel-example-value value={this.value} />,
        ];
    }

    private handleInput = (event: CustomEvent<string>) => {
        this.textValue = event.detail;
    };

    private onKeyUp = (event: KeyboardEvent) => {
        if (
            (event.key === ENTER || event.keyCode === ENTER_KEY_CODE) &&
            this.textValue.trim()
        ) {
            this.value = [
                ...this.value,
                this.createChip(this.textValue.trim()),
            ];
            this.textValue = '';
        }
    };

    private handleChange = (event: CustomEvent<Chip[]>) => {
        console.log(event.detail);
        this.value = event.detail;
    };

    private handleInteraction = (event: CustomEvent<Chip>) => {
        console.log('Chip interacted with: ', event.detail);
    };

    private createChip = (name: string): Chip => {
        return {
            id: name,
            text: name,
            removable: true,
            icon: `${name}`.toLowerCase(),
        };
    };

    private setDisabled = (event: CustomEvent<boolean>) => {
        this.disabled = event.detail;
    };

    private setReadonly = (event: CustomEvent<boolean>) => {
        this.readonly = event.detail;
    };

    private setRequired = (event: CustomEvent<boolean>) => {
        this.required = event.detail;
    };

    private setEmptyInputOnBlur = (event: CustomEvent<boolean>) => {
        this.emptyInputOnBlur = event.detail;
    };

    private setLeadingIcon = (event: CustomEvent<boolean>) => {
        this.hasLeadingIcon = event.detail;
    };

    private setMaxItems = (event: CustomEvent<string>) => {
        this.maxItems = +event.detail;
    };

    private useDelimiters = (event: CustomEvent<boolean>) => {
        this.delimiter = event.detail ? '&' : null;
    };
}

