import { Component, h, State } from '@stencil/core';
import { schema } from './custom-component-schema';

/**
 * Custom form component
 *
 * You can specify a custom component to use for any property in your form. This
 * is done under the `lime` key in the schema, following the
 * [LimeSchemaOptions](#/type/LimeSchemaOptions/) specification, for example:
 *
 * ```ts
 * const schema = {
 *     type: 'object',
 *     properties: {
 *         hero: {
 *             type: 'integer',
 *             title: 'Hero',
 *             lime: {
 *                 component: {
 *                     name: 'my-useful-hero-picker',
 *                 },
 *             },
 *         },
 *     },
 * };
 * ```
 *
 * While you can, in principle, use any component in a form, your custom form
 * components should implement the [FormComponent](#/type/FormComponent/)
 * interface.
 *
 * @link custom-component-schema.ts
 * @link custom-component-picker.tsx
 */
@Component({
    tag: 'limel-example-custom-component-form',
    shadow: true,
})
export class CustomComponentFormExample {
    @State()
    private formData: object = {
        name: 'My superhero deal',
        value: 1000000,
        hero: 1001,
    };

    public render() {
        return [
            <limel-form
                onChange={this.handleFormChange}
                value={this.formData}
                schema={schema}
            />,
            <limel-example-value value={this.formData} />,
        ];
    }

    private handleFormChange = (event) => {
        this.formData = event.detail;
    };
}

export const schema = {
    type: 'object',
    properties: {
        name: {
            type: 'string',
            title: 'Name',
            description: 'Name of the superhero deal',
        },
        value: {
            type: 'integer',
            title: 'Value',
            description: 'Amount to pay to hero',
        },
        hero: {
            type: 'integer',
            title: 'Hero',
            lime: {
                component: {
                    name: 'limel-example-custom-picker',
                },
            },
        },
    },
};

import { Component, h, Prop, EventEmitter, Event } from '@stencil/core';
import { FormComponent } from '@limetech/lime-elements';
import { ListItem } from '../../list/list-item.types';

@Component({
    tag: 'limel-example-custom-picker',
    shadow: true,
})
export class CustomPickerExample implements FormComponent<number> {
    /**
     * The value of the property
     */
    @Prop()
    public value: number;

    /**
     * Label to display next to the input field
     */
    @Prop()
    public label: string;

    /**
     * Set to `true` if a value is required
     */
    @Prop()
    public required: boolean;

    /**
     * Set to `true` if the value is readonly
     */
    @Prop()
    public readonly: boolean;

    /**
     * Set to `true` if input should be disabled
     */
    @Prop()
    public disabled: boolean;

    /**
     * Emitted when the value is changed
     */
    @Event()
    public change: EventEmitter<number>;

    private heroes: Array<ListItem<number>> = [
        {
            text: 'Superman',
            value: 1001,
            icon: 'superman',
            iconColor: 'var(--lime-deep-red)',
        },
        {
            text: 'Squirrel Girl',
            value: 1002,
            icon: 'squirrel',
            iconColor: 'var(--lime-orange)',
        },
        {
            text: 'Captain America',
            value: 1003,
            icon: 'captain_america',
            iconColor: 'var(--lime-blue)',
        },
        {
            text: 'Black Widow',
            value: 1004,
            icon: 'spider',
            iconColor: 'var(--lime-dark-grey)',
        },
    ];

    private handleChange = (event: CustomEvent<ListItem<number>>) => {
        event.stopPropagation();
        this.change.emit(event.detail?.value);
    };

    private search = async (query: string): Promise<ListItem[]> => {
        return this.heroes.filter((hero) => {
            return hero.text.toLowerCase().includes(query.toLowerCase());
        });
    };

    public render() {
        const value = this.heroes.find((hero) => hero.value === this.value);

        return (
            <limel-picker
                label={this.label}
                value={value}
                disabled={this.disabled}
                readonly={this.readonly}
                required={this.required}
                onChange={this.handleChange}
                searcher={this.search}
            />
        );
    }
}

import { ValidationStatus, FormError } from '@limetech/lime-elements';
import { Component, h, State } from '@stencil/core';
import { schema } from './custom-error-message-schema';

/**
 * Form with custom error message
 *
 * @link custom-error-message-schema.ts
 */
@Component({
    tag: 'limel-example-custom-error-message',
    shadow: true,
})
export class CustomErrorMessageFormExample {
    @State()
    private formData: object = {
        personalIdentityNumber: '',
    };

    @State()
    private valid = true;

    public render() {
        return [
            <limel-form
                onValidate={this.handleFormValidate}
                onChange={this.handleFormChange}
                value={this.formData}
                schema={schema}
                transformErrors={this.transformErrors}
            />,
            <br />,
            <limel-button
                label="Submit"
                primary={true}
                disabled={!this.valid}
                onClick={this.handleSubmit}
            />,
        ];
    }

    private handleFormChange = (event) => {
        this.formData = event.detail;
    };

    private handleSubmit = () => {
        const json = JSON.stringify(this.formData, null, '    ');
        alert(`Sending information to villains...\n\n${json}`);
    };

    private handleFormValidate = (event: CustomEvent<ValidationStatus>) => {
        this.valid = event.detail.valid;
    };

    private transformErrors = (errors: FormError[]): FormError[] => {
        return errors.map((error) => {
            if (
                error.name === 'pattern' &&
                error.property === '.personalIdentityNumber'
            ) {
                error.message = 'Invalid format, use YYYYMMDD-NNNN';
            }

            return error;
        });
    };
}

export const schema = {
    title: 'Personal identity number form',
    description: 'Please enter your personal identity number',
    type: 'object',
    required: ['personalIdentityNumber'],
    properties: {
        personalIdentityNumber: {
            type: 'string',
            title: 'Personal identity number',
            description: 'Enter your personal identity number',
            pattern: '[0-9]{8}[-][0-9]{4}',
        },
    },
};

